module body() {
    // Main torso - elongated ellipsoid
    scale([1.2, 0.8, 1])
        sphere(12);

    // Neck extension
    translate([9, 0, 2])
        rotate([0, -20, 0])
        scale([0.8, 0.6, 0.6])
            cylinder(h=10, r1=4, r2=3);
}

module head() {
    // Head - slightly squashed sphere
    translate([18, 0, 3])
        scale([1.2, 1, 0.9])
            sphere(5);

    // Beak
    translate([23, 0, 2])
        rotate([0, -15, 0])
        scale([1, 0.8, 0.6])
            cylinder(h=8, r1=2, r2=0.5);

    // Head crest
    translate([17, 0, 7])
        rotate([20, 0, 0])
        scale([0.3, 0.8, 1])
            cylinder(h=3, r=3);
}

module wing(side) {
    mirror([side, 0, 0]) {
        // Shoulder joint
        translate([-10, 0, 5])
            sphere(3);

        // Upper wing
        translate([-25, -side*25, 2])
            rotate([0, 0, side*15])
            scale([2.5, 0.3, 0.8])
                cylinder(h=25, r=4);

        // Lower wing
        translate([-45, -side*35, -2])
            rotate([0, 0, side*10])
            scale([2, 0.2, 0.6])
                cylinder(h=20, r=3);

        // Wing tip feathers
        for(i = [0:2]) {
            translate([-55, -side*(38+i*3), -3])
                rotate([0, 0, side*(5+i*5)])
                scale([0.5, 0.1, 0.4])
                    cylinder(h=8, r=2);
        }
    }
}

module tail() {
    // Tail base
    translate([-12, 0, -2])
        rotate([10, 0, 0])
        scale([1, 0.3, 0.5])
            cylinder(h=8, r=5);

    // Tail feathers
    for(i = [-1:1]) {
        translate([-15, i*3, -3])
            rotate([20, 0, 0])
            scale([1.5, 0.2, 0.3])
                cylinder(h=12, r=2);
    }
}

module legs() {
    // Left leg
    translate([0, 4, -10])
        rotate([10, 0, 0])
        cylinder(h=12, r=1.5);

    // Left foot
    translate([0, 4, -22])
        rotate([90, 0, 0])
        cylinder(h=8, r=2);

    // Left toes
    for(i = [-1:1]) {
        translate([0, 4+i*1.5, -22])
            rotate([90, 0, 0])
            scale([0.3, 0.8, 1])
                cylinder(h=6, r=0.8);
    }

    // Right leg (mirrored)
    mirror([0, 1, 0]) {
        translate([0, 4, -10])
            rotate([10, 0, 0])
            cylinder(h=12, r=1.5);

        translate([0, 4, -22])
            rotate([90, 0, 0])
            cylinder(h=8, r=2);

        for(i = [-1:1]) {
            translate([0, 4+i*1.5, -22])
                rotate([90, 0, 0])
                scale([0.3, 0.8, 1])
                    cylinder(h=6, r=0.8);
        }
    }
}

// Assembly
union() {
    body();
    head();
    wing(1);    // Right wing
    wing(-1);   // Left wing
    tail();
    legs();
}

