$fn = 36;

module conure() {
    // Body
    union() {
        // Main body
        translate([0, 0, 15])
            scale([1.2, 0.9, 1.3])
            sphere(15);

        // Belly
        translate([0, -12, 8])
            scale([0.8, 0.7, 0.9])
            sphere(12);

        // Back
        translate([0, 8, 18])
            scale([1.1, 0.6, 1.2])
            sphere(10);
    }

    // Head
    translate([0, 0, 40])
        scale([0.9, 0.9, 1.1])
        sphere(12);

    // Beak
    translate([0, -18, 42])
        rotate([-30, 0, 0])
        scale([1, 0.8, 2.5])
        cylinder(8, 3, 1);

    // Beak upper part
    translate([0, -18, 44])
        rotate([-30, 0, 0])
        scale([0.8, 0.6, 2])
        cylinder(7, 2.5, 0.5);

    // Eyes
    for(side = [-1, 1]) {
        translate([side * 5, -10, 42])
            sphere(2);
    }

    // Eye rings
    for(side = [-1, 1]) {
        translate([side * 5, -10, 42])
            scale([1.2, 1, 1])
            cylinder(0.5, 3.5, 3.5);
    }

    // Wings
    for(side = [-1, 1]) {
        translate([side * 15, 5, 30])
            rotate([0, side * 20, 0])
            scale([0.3, 1.2, 0.8])
            cylinder(15, 5, 3);
    }

    // Tail feathers
    for(i = [0:2]) {
        translate([0, 20 - i * 3, 15 - i * 2])
            rotate([0, 0, -15 + i * 15])
            scale([0.3, 1, 0.1])
            cube([2, 20, 5]);
    }

    // Feet
    for(side = [-1, 1]) {
        // Leg
        translate([side * 8, -5, 5])
            cylinder(8, 2, 2);

        // Toes
        for(angle = [0, 30, -30]) {
            translate([side * 8, -5, 0])
                rotate([0, 0, angle])
                cylinder(6, 1.5, 1.5);
        }
    }

    // Crest feathers
    for(i = [0:4]) {
        translate([0, 0, 52 + i * 3])
            rotate([0, 0, -20 + i * 10])
            scale([0.2, 1, 0.1])
            cube([1, 8, 3]);
    }
}

// Generate the conure
conure();

