// Clownfish - Realistic Medium Complexity
// Approx 70mm long, 3D printable

$fn = 36;

// Main colors
body_color = "#FF6B00";
stripe_color = "#FFFFFF";
fin_color = "#FF4D00";
eye_color = "#000000";

// Overall dimensions
body_length = 70;
body_height = 35;
body_width = 25;

module clownfish() {
    // Body - main oval shape
    union() {
        // Main body
        scale([1, 0.8, 0.6])
        sphere(d=body_length);

        // Head bump
        translate([body_length/2 - 5, 0, 0])
        sphere(d=body_width * 0.8);

        // Tail base
        translate([-body_length/2 + 5, 0, 0])
        sphere(d=body_width * 0.7);
    }

    // White stripes
    difference() {
        // Body for stripes
        scale([1, 0.8, 0.6])
        sphere(d=body_length);

        // Stripe 1 - behind head
        translate([body_length/4, 0, -body_height/2])
        scale([1.1, 1.1, 0.5])
        cube([8, body_width * 1.5, body_height], center=true);

        // Stripe 2 - middle body
        translate([-body_length/4, 0, -body_height/2])
        scale([1.1, 1.1, 0.5])
        cube([8, body_width * 1.5, body_height], center=true);

        // Stripe 3 - tail base
        translate([-body_length/2 + 8, 0, -body_height/2])
        scale([1.1, 1.1, 0.5])
        cube([6, body_width * 1.3, body_height], center=true);
    }

    // Dorsal fin
    translate([0, 0, body_height/2 - 2])
    rotate([0, 0, 0])
    scale([1.2, 0.3, 0.8])
    cylinder(h=body_length * 0.6, d1=body_width, d2=body_width * 0.6);

    // Pectoral fins (left and right)
    for (side = [-1, 1]) {
        translate([body_length/4, side * body_width/2, -body_height/4])
        rotate([0, side * 30, 0])
        scale([0.8, 0.2, 0.6])
        cylinder(h=body_width * 0.7, d1=body_width * 0.5, d2=body_width * 0.3);
    }

    // Anal fin
    translate([0, 0, -body_height/2 + 2])
    rotate([0, 0, 0])
    scale([1, 0.3, 0.6])
    cylinder(h=body_length * 0.4, d1=body_width * 0.8, d2=body_width * 0.4);

    // Tail fin
    translate([-body_length/2 - 5, 0, 0])
    rotate([0, 90, 0])
    scale([1.2, 1, 0.1])
    cylinder(h=body_height * 1.2, d1=body_width * 0.8, d2=body_width * 1.2);

    // Eyes
    for (side = [-1, 1]) {
        // Eye white
        translate([body_length/2 - 8, side * body_width/3, body_height/4])
        sphere(d=body_width * 0.2);

        // Eye pupil
        translate([body_length/2 - 7, side * body_width/3, body_height/4])
        sphere(d=body_width * 0.1);
    }

    // Mouth
    translate([body_length/2, 0, -body_height/4])
    rotate([0, 90, 0])
    cylinder(h=2, d=body_width * 0.3);

    // Lips detail
    translate([body_length/2 + 1, 0, -body_height/4])
    rotate([0, 90, 0])
    cylinder(h=1, d=body_width * 0.35);
}

// Color modules for display
module colored_body() {
    color(body_color) children();
}

module colored_fin() {
    color(fin_color) children();
}

module colored_stripe() {
    color(stripe_color) children();
}

module colored_eye() {
    color(eye_color) children();
}

// Assemble with colors
colored_body() clownfish();

