    // Chest protrusion
    translate([body_length/4, 0, -body_height/4])
    rotate([0, 15, 0])
    scale([0.4, 0.6, 0.5])
    sphere(d=body_height/2, $fn=24);
}

module head() {
    // Skull
    translate([-body_length/2 - head_size/2, 0, body_height/3])
    rotate([0, -10, 0])
    sphere(d=head_size, $fn=32);

    // Muzzle
    translate([-body_length/2 - head_size, 0, body_height/3 - head_size/3])
    rotate([0, -10, 0])
    scale([0.7, 0.6, 0.5])
    sphere(d=head_size * 0.8, $fn=24);

    // Eyes
    for(side = [-1, 1]) {
        translate([-body_length/2 - head_size/1.5, side * head_size/3, body_height/3 + head_size/6])
        sphere(d=head_size/8, $fn=16);
    }

    // Nose
    translate([-body_length/2 - head_size * 1.1, 0, body_height/3 - head_size/6])
    sphere(d=head_size/12, $fn=12);
}

module limbs() {
