    for(side = [-1, 1]) {
        // Upper arm
        translate([-body_length/4, side * body_width/2, 0])
        rotate([0, 0, side * 30])
        rotate([45, 0, 0])
        cylinder(h=limb_length/2, d1=body_width/3, d2=body_width/4, $fn=12);

        // Lower arm
        translate([-body_length/4 - limb_length/3, side * body_width/1.3, -limb_length/3])
        rotate([0, 0, side * 30])
        rotate([90, 0, 0])
        cylinder(h=limb_length/2, d1=body_width/4, d2=body_width/5, $fn=12);
    }

    // Legs
    for(side = [-1, 1]) {
        // Upper leg
        translate([body_length/3, side * body_width/2, -body_height/3])
        rotate([0, 0, side * -20])
        rotate([30, 0, 0])
        cylinder(h=limb_length/2, d1=body_width/3, d2=body_width/4, $fn=12);

        // Lower leg
        translate([body_length/3 + limb_length/3, side * body_width/1.5, -body_height/2 - limb_length/3])
        rotate([0, 0, side * -20])
        rotate([90, 0, 0])
        cylinder(h=limb_length/2, d1=body_width/4, d2=body_width/5, $fn=12);
    }
}

module tail() {
    // Base of tail
    translate([body_length/2, 0, -body_height/4])
    rotate([0, 0, -20])
    cylinder(h=tail_length/4, d1=body_width/3, d2=body_width/4, $fn=12);

    // Mid tail
    translate([body_length/2 + tail_length/4, 0, -body_height/3])
    rotate([0, 0, -30])
    cylinder(h=tail_length/4, d1=body_width/4, d2=body_width/5, $fn=12);

    // Tail tip
    translate([body_length/2 + tail_length/2, 0, -body_height/2])
    rotate([0, 0, -45])
    cylinder(h=tail_length/2, d1=body_width/5, d2=body_width/10, $fn=12);
