// Cormorant Model - Realistic Medium Complexity
// Wings folded, perched pose for easy printing

// Overall scale
scale_factor = 1.5;

// Body parameters
body_length = 40 * scale_factor;
body_height = 20 * scale_factor;
body_width = 15 * scale_factor;

// Head parameters
head_diameter = 12 * scale_factor;
neck_length = 15 * scale_factor;
neck_diameter = 5 * scale_factor;

// Beak parameters
beak_length = 18 * scale_factor;
beak_base = 4 * scale_factor;
beak_tip = 1 * scale_factor;

// Tail parameters
tail_length = 15 * scale_factor;
tail_width = 20 * scale_factor;
tail_height = 3 * scale_factor;

// Leg parameters
leg_length = 18 * scale_factor;
leg_diameter = 3 * scale_factor;
foot_length = 12 * scale_factor;
foot_width = 4 * scale_factor;

// Wing parameters
wing_length = 25 * scale_factor;
wing_width = 10 * scale_factor;

module cormorant_body() {
    // Main body - elongated ellipsoid
    scale([1, 0.7, 0.5])
    sphere(d=body_length, $fn=32);

    // Chest protrusion
    translate([body_length/4, 0, -body_height/4])
    scale([0.6, 0.8, 0.4])
    sphere(d=body_height * 1.2, $fn=24);
}

module cormorant_neck() {
    // Neck - tapered cylinder
    translate([-body_length/2 + 5, 0, body_height/2])
    rotate([0, -20, 0])
    cylinder(h=neck_length, d1=neck_diameter*1.2, d2=neck_diameter, $fn=16);
}

module cormorant_head() {
    // Head - sphere with flattened sides
    translate([-body_length/2 - neck_length + 2, 0, body_height/2 + neck_length/2])
    rotate([0, -20, 0])
    scale([1, 0.8, 0.9])
    sphere(d=head_diameter, $fn=24);

    // Eye sockets
    for(side = [-1, 1]) {
        translate([-body_length/2 - neck_length, side * head_diameter/3, body_height/2 + neck_length/2])
        rotate([0, -20, 0])
        translate([0, -head_diameter/4, head_diameter/4])
        sphere(d=head_diameter/6, $fn=12);
    }
}

module cormorant_beak() {
    // Upper beak
    translate([-body_length/2 - neck_length - beak_length/2 + 2, 0, body_height/2 + neck_length/2 - 2])
    rotate([0, -20, 0])
    rotate([15, 0, 0])
    scale([1, 0.8, 0.5])
    cylinder(h=beak_length, d1=beak_base, d2=beak_tip, $fn=16);

    // Lower beak
    translate([-body_length/2 - neck_length - beak_length/2 + 2, 0, body_height/2 + neck_length/2 - 4])
    rotate([0, -20, 0])
    rotate([-10, 0, 0])
    scale([1, 0.7, 0.4])
    cylinder(h=beak_length * 0.9, d1=beak_base * 0.9, d2=beak_tip * 0.8, $fn=16);
}

module cormorant_tail() {
    // Tail fan
    translate([body_length/2 - 2, 0, -body_height/4])
    rotate([0, -30, 0])
    scale([1, 0.3, 0.5])
    cube([tail_length, tail_width, tail_height], center=true);

    // Tail feathers
    for(i = [-2:2]) {
        translate([body_length/2 + tail_length/2 - 2, i * 4, -body_height/4])
        rotate([0, -30 + i * 5, 0])
        cube([tail_length/2, 2, tail_height/2], center=true);
    }
}

module cormorant_wing() {
    // Folded wing - simplified
    translate([-body_length/4, body_width/2 + 2, 0])
    rotate([0, 0, -30])
    scale([1, 0.2, 0.6])
    cube([wing_length, wing_width, wing_width/2], center=true);
}

module cormorant_leg() {
    // Upper leg
    translate([-body_length/4, 0, -body_height/2 - leg_length/2])
    rotate([15, 0, 0])
    cylinder(h=leg_length/2, d=leg_diameter, $fn=12);

    // Lower leg
    translate([-body_length/4, 0, -body_height/2 - leg_length])
    rotate([75, 0, 0])
    cylinder(h=leg_length/2, d=leg_diameter * 0.8, $fn=12);

    // Webbed foot
    translate([-body_length/4 - foot_length/2, 0, -body_height/2 - leg_length - 2])
    rotate([90, 0, 0])
    scale([1, 0.3, 1])
    cube([foot_length, foot_width, 2], center=true);

    // Toes
    for(i = [-1.5, 0, 1.5]) {
        translate([-body_length/4 + foot_length/2 - 2, i * foot_width/3, -body_height/2 - leg_length - 2])
        rotate([90, 0, 0])
        scale([0.3, 1, 1])
        cube([6, 1.5, 1.5], center=true);
    }
}

// Assembly
union() {
    // Main body parts
    cormorant_body();
    cormorant_neck();
    cormorant_head();
    cormorant_beak();
    cormorant_tail();

    // Wings
    for(side = [-1, 1]) {
        mirror([0, side, 0])
        cormorant_wing();
    }

    // Legs
    for(side = [-1, 1]) {
        translate([0, side * body_width/3, 0])
        cormorant_leg();
    }
}

