// Cowbird - Medium complexity realistic style
// Size: ~70mm long, designed for 3D printing

// Body dimensions
body_length = 35;
body_width = 20;
body_height = 25;

// Head dimensions
head_diameter = 15;

// Leg dimensions
leg_length = 12;
leg_width = 3;

// Wing dimensions
wing_length = 30;
wing_width = 15;

// Tail dimensions
tail_length = 25;
tail_width = 20;

// Beak dimensions
beak_length = 8;
beak_width = 4;
beak_height = 3;

module bird_body() {
    // Main oval body
    scale([1.3, 1, 1.1])
    sphere(d=body_length, $fn=32);
}

module bird_head() {
    // Slightly flattened sphere for head
    translate([body_length/2 + head_diameter/3, 0, body_height/3])
    scale([1.1, 1, 0.9])
    sphere(d=head_diameter, $fn=32);

    // Beak - conical
    translate([body_length/2 + head_diameter/2 + beak_length/2, 0, body_height/3])
    rotate([0, -10, 0])
    cylinder(h=beak_length, d1=beak_width, d2=beak_width/2, $fn=8);
}

module bird_wings() {
    // Left wing
    translate([body_length/4, -body_width/2 - wing_width/3, body_height/3])
    rotate([0, 0, -20])
    scale([1, 0.7, 0.2])
    difference() {
        sphere(d=wing_length, $fn=32);
        translate([wing_length/2, 0, 0])
        cube([wing_length, wing_width, wing_length], center=true);
    }

    // Right wing
    mirror([0, 1, 0])
    translate([body_length/4, -body_width/2 - wing_width/3, body_height/3])
    rotate([0, 0, -20])
    scale([1, 0.7, 0.2])
    difference() {
        sphere(d=wing_length, $fn=32);
        translate([wing_length/2, 0, 0])
        cube([wing_length, wing_width, wing_length], center=true);
    }
}

module bird_tail() {
    // Fan-shaped tail
    translate([-body_length/2 - tail_length/2, 0, 0])
    rotate([0, 30, 0])
    scale([1, 0.3, 0.8])
    difference() {
        sphere(d=tail_length, $fn=32);
        translate([tail_length/2, 0, 0])
        cube([tail_length, tail_width, tail_length], center=true);
    }
}

module bird_legs() {
    // Left leg
    translate([0, -body_width/4, -body_height/2 - leg_length/2])
    rotate([5, 0, 0])
    cylinder(h=leg_length, d=leg_width, $fn=12);

    // Left foot
    translate([0, -body_width/4 - leg_width, -body_height/2 - leg_length])
    cube([leg_width*2, leg_width*3, leg_width/2]);

    // Right leg
    translate([0, body_width/4, -body_height/2 - leg_length/2])
    rotate([5, 0, 0])
    cylinder(h=leg_length, d=leg_width, $fn=12);

    // Right foot
    translate([0, body_width/4 + leg_width, -body_height/2 - leg_length])
    cube([leg_width*2, leg_width*3, leg_width/2]);
}

module bird_eyes() {
    // Left eye
    translate([body_length/2 + head_diameter/3 + head_diameter/4, 
               -head_diameter/4, 
               body_height/3 + head_diameter/6])
    sphere(d=2, $fn=16);

    // Right eye
    translate([body_length/2 + head_diameter/3 + head_diameter/4, 
               head_diameter/4, 
               body_height/3 + head_diameter/6])
    sphere(d=2, $fn=16);
}

// Main assembly
union() {
    bird_body();
    bird_head();
    bird_wings();
    bird_tail();
    bird_legs();
    bird_eyes();
