// Coot (Fulica atra) – realistic medium-complexity OpenSCAD model
// Overall length ≈ 80 mm, no overhangs > 45°, 1.2 mm wall thickness

body_len = 60;
body_w   = 30;
body_h   = 18;
wall     = 1.2;

module oval(t,l,w,h) scale([l,w,h]) sphere(t,$fn=32);

// Main body – tapered oval
module body() {
    difference(){
        union(){
            translate([0,0,body_h/2])
                oval(body_w/2, body_len, body_w, body_h);
            // Slight tail rise
            translate([-body_len/2-5,0,0])
                rotate([0,15,0])
                oval(body_w/2,15,body_w*0.7,body_h*0.7);
        }
        // Hollow
        translate([0,0,wall])
            oval(body_w/2-wall, body_len-wall*2, body_w-wall*2, body_h-wall);
    }
}

// Head
module head() {
    translate([body_len/2+12,0,body_h/2+5])
    rotate([0,-20,0])
    difference(){
        scale([1,0.9,1]) sphere(9,$fn=32);
        translate([0,0,wall]) sphere(9-wall,$fn=32);
    }
}

// Beak with frontal shield
module beak() {
    translate([body_len/2+24,0,body_h/2+4])
    rotate([0,-20,0])
    union(){
        // Upper mandible
        translate([4,0,0])
            rotate([0,20,0])
            scale([10,3,4])
            cylinder(h=1,$fn=16);
        // Lower mandible
        translate([4,0,-2])
            rotate([0,-10,0])
            scale([8,2.5,2])
            cylinder(h=1,$fn=16);
        // White frontal shield
        color("white")
        translate([0,0,1])
            scale([6,6,5])
            sphere(1,$fn=32);
    }
}

// Eye
module eye() {
    translate([body_len/2+14,5.5,body_h/2+7])
        sphere(1.5,$fn=16);
    translate([body_len/2+14,-5.5,body_h/2+7])
        sphere(1.5,$fn=16);
}

// Leg segment
module leg_seg(len,r){
    difference(){
        cylinder(h=len, r=r, $fn=16);
        translate([0,0,wall]) cylinder(h=len-wall, r=r-wall, $fn=16);
    }
}

// Webbed foot
module foot() {
    translate([0,0,0])
    difference(){
        hull(){
            cube([12,wall*2,4]);
            translate([9,0,0]) cube([wall*2,18,4]);
            translate([-9,0,0]) cube([wall*2,18,4]);
        }
        // Remove bottom to avoid overhang
        translate([0,0,-2]) cube([20,20,2.1],center=true);
    }
}

// Full leg
module leg(side) {
    // Thigh
    translate([body_len/2-5, side*10, -body_h/2-2])
    rotate([20,0,side*-10])
    leg_seg(12,2);

    // Shank
    translate([body_len/2-5+10*cos(20), side*10+side*10*sin(20), -body_h/2-2-12*sin(20)])
    rotate([0,0,side*-5])
    leg_seg(15,1.5);

    // Foot
    translate([body_len/2-5+10*cos(20)+15*sin(0), side*10+side*10*sin(20)+side*15, -body_h/2-2-12*sin(20)-15*cos(0)])
    rotate([90,0,0])
    foot();
}

// Wings – low-profile to avoid steep overhangs
module wing(side) {
    difference(){
        translate([0, side*body_w/2, body_h/2])
        rotate([0,10,0])
        scale([body_len*0.7,body_w*0.4,body_h*0.3])
            oval(1,1,1,1);
        // Hollow
        translate([0,side*wall,wall])
        scale([body_len*0.7-wall*2,body_w*0.4-wall*2,body_h*0.3-wall])
            oval(1,1,1,1);
    }
}

// Assembly
body();
head();
beak();
eye();
wing(1);
wing(-1);
leg(1);
leg(-1);

