// claw reach adds 15 mm each side
claw_reach = 15;

// Wall thickness for printing
wall = 1.5;

// Main module
module crab() {
    // Whole crab pivoted slightly for printing
    rotate([0,15,0])
    union() {
        body();
        legs();
        claws();
        eyes();
