// Realistic Crake - Medium Complexity
// 3D printable model with proper wall thickness

// Main parameters
body_length = 55;
body_height = 25;
head_size = 12;
leg_height = 18;
wall_thickness = 1.2;

// Module for the main body
module body() {
    // Main torso - elongated ellipsoid shape
    difference() {
        // Main body form
        hull() {
            translate([-body_length/2, 0, 0])
                scale([1.2, 0.8, 0.6])
                sphere(d=body_height);
            translate([body_length/2-5, 0, 0])
                scale([0.8, 0.7, 0.5])
                sphere(d=body_height*0.8);
        }

        // Hollow out for printing
        translate([0, 0, -2])
            scale([0.8, 0.7, 0.5])
            sphere(d=body_height*0.9);
    }
}

// Module for the head
module head() {
    translate([body_length/2+8, 0, body_height/2]) {
        // Main head shape
        difference() {
            rotate([0, -15, 0])
                scale([0.9, 0.8, 0.7])
                sphere(d=head_size);

            // Eye sockets
            translate([head_size/3, head_size/3, 0])
                sphere(d=head_size/3);
            translate([head_size/3, -head_size/3, 0])
                sphere(d=head_size/3);
        }

        // Beak
        translate([head_size/2, 0, -head_size/4])
            rotate([0, -30, 0])
            scale([1.5, 0.3, 0.3])
            cylinder(h=head_size, d1=head_size/2, d2=0);
    }
}

// Module for legs and feet
module legs() {
    // Left leg
    translate([-body_length/3, body_height/2, -body_height/2]) {
        // Upper leg
        rotate([0, -30, 15])
            cylinder(h=leg_height/2, d=wall_thickness*1.5);

        // Lower leg
        translate([0, -leg_height/4, -leg_height/2.5])
            rotate([0, 30, 0])
            cylinder(h=leg_height/2, d=wall_thickness*1.2);

        // Foot
        translate([0, -leg_height/2, -leg_height*0.8])
            rotate([90, 0, 0])
            scale([2, 0.5, 1])
            cylinder(h=wall_thickness*2, d=wall_thickness*3);

        // Toes
        for(i=[-1, 0, 1]) {
            translate([i*3, -leg_height/2-3, -leg_height*0.8])
                rotate([90, 0, 0])
                scale([1.2, 0.4, 0.6])
                cylinder(h=4, d=wall_thickness);
        }
    }

    // Right leg (mirror of left)
    translate([-body_length/3, -body_height/2, -body_height/2]) {
        mirror([0, 1, 0]) {
            // Upper leg
            rotate([0, -30, 15])
                cylinder(h=leg_height/2, d=wall_thickness*1.5);

            // Lower leg
            translate([0, -leg_height/4, -leg_height/2.5])
                rotate([0, 30, 0])
                cylinder(h=leg_height/2, d=wall_thickness*1.2);

            // Foot
            translate([0, -leg_height/2, -leg_height*0.8])
                rotate([90, 0, 0])
                scale([2, 0.5, 1])
                cylinder(h=wall_thickness*2, d=wall_thickness*3);

            // Toes
            for(i=[-1, 0, 1]) {
                translate([i*3, -leg_height/2-3, -leg_height*0.8])
                    rotate([90, 0, 0])
                    scale([1.2, 0.4, 0.6])
                    cylinder(h=4, d=wall_thickness);
            }
        }
    }
}

// Module for tail feathers
module tail() {
    translate([-body_length/2-5, 0, -body_height/4]) {
        hull() {
            translate([0, 0, 0])
                scale([1, 0.3, 0.1])
                sphere(d=body_height/2);
            translate([-8, 0, 2])
                scale([0.5, 0.8, 0.05])
                sphere(d=body_height/2);
        }
    }
}

// Module for wing feathers
module wings() {
    // Left wing
    translate([0, body_height/2+2, body_height/4]) {
        rotate([0, 0, -20])
            scale([1.2, 0.3, 0.4])
            sphere(d=body_height);
    }

    // Right wing
    translate([0, -body_height/2-2, body_height/4]) {
        rotate([0, 0, 20])
            mirror([0, 1, 0])
            scale([1.2, 0.3, 0.4])
            sphere(d=body_height);
    }
}

// Assembly
union() {
    body();
    head();
    legs();
    tail();
    wings();

    // Eye details
    translate([body_length/2+8+head_size/3, head_size/3, body_height/2]) {
        color("black")
        sphere(d=head_size/4);
    }
    translate([body_length/2+8+head_size/3, -head_size/3, body_height/2]) {
        color("black")
        sphere(d=head_size/4);
    }
}

