// Realistic 3D printable crow (medium complexity)
// Overall length ~70 mm

$fn = 50;  // Smooth curves for printing

module crow() {
    // Body - main oval
    translate([0, -15, 5]) rotate([90, 0, 0]) scale([1, 0.7, 1.2]) sphere(15);

    // Head
    translate([0, 12, 18]) sphere(10);

    // Beak
    difference() {
        translate([-2, 22, 18]) rotate([0, 0, -10]) cylinder(h=12, r1=3, r2=1);
        translate([-2, 22, 18]) rotate([0, 0, -10]) translate([0, 0, -1]) cylinder(h=14, r1=2.5, r2=0.5);
    }

    // Eye
    translate([4, 14, 20]) sphere(2);

    // Tail - fan shape
    for(i = [-20:10:20]) {
        rotate([0, 0, i]) translate([0, -35, 5]) cube([1, 20, 3]);
    }

    // Wings
    module wing(side) {
        mirror([side, 0, 0]) {
            // Main wing
            translate([-5, -5, 8]) rotate([0, 0, 30]) rotate([30, 0, 0]) 
                scale([1, 0.3, 2]) sphere(10);

            // Wing feathers
            for(i = [0:8]) {
                rotate([0, 0, -20 + i*10]) 
                translate([-15 - i*2, -8 + i, 8 - i*0.5]) 
                cube([2, 12 - i*0.5, 1]);
            }
        }
    }

    wing(1);
    wing(-1);

    // Legs
    module leg(side) {
        translate([side*8, 0, 0]) {
            // Thigh
            translate([0, -5, 0]) rotate([0, 30, 0]) cylinder(h=8, r=2);

            // Lower leg
            translate([0, -5, -6]) rotate([0, 30, 0]) cylinder(h=10, r=1.5);

            // Feet - 3 toes forward, 1 back
            translate([0, -5, -16]) {
                for(angle = [0, 30, -30, 180]) {
                    rotate([0, 0, angle]) {
                        rotate([0, 90, 0]) cylinder(h=8, r=1);
                    }
                }
            }
        }
    }

    leg(1);
    leg(-1);
}

