    // Torso - elongated sphere
    scale([1.5, 0.7, 0.8])
    sphere(r=15, $fn=32);
}

// Head module with facial features
module head() {
    // Main head shape
    translate([20, 0, 10])
    scale([1.2, 0.9, 0.7])
    sphere(r=12, $fn=32);

    // Snout
    translate([28, 0, 8])
    scale([0.8, 0.6, 0.5])
    sphere(r=8, $fn=32);

    // Ears
    for(side = [-1, 1]) {
        translate([15, side*8, 18])
        rotate([0, side*15, 0])
        scale([0.5, 0.3, 1])
        cylinder(h=6, r1=4, r2=2, $fn=16);
    }

    // Eyes
    for(side = [-1, 1]) {
        translate([22, side*5, 12])
        scale([0.3, 0.3, 0.3])
        sphere(r=2, $fn=16);
    }
}

// Leg module - parameterized for all legs
module leg(x_pos, y_pos, z_pos, angle) {
    translate([x_pos, y_pos, z_pos])
    rotate([0, 0, angle]) {
        // Upper leg
        rotate([-30, 0, 0])
        cylinder(h=15, r1=4, r2=3, $fn=16);

        // Lower leg
        translate([0, -12, -10])
        rotate([30, 0, 0])
        cylinder(h=12, r1=3, r2=2, $fn=16);

        // Paw
        translate([0, -20, -15])
        sphere(r=3, $fn=16);
    }
}

