    rotate([0, -10, 0])
    scale([1.5, 0.6, 0.3])
    cylinder(h=12, r1=6, r2=2);
}

// Head with crossed bill
module head() {
    // Head sphere
    translate([-12, 0, 12])
    sphere(8);

    // Upper mandible
    translate([-18, 0, 14])
    rotate([0, 0, -25])
    scale([0.3, 0.3, 1])
    cylinder(h=15, r1=2, r2=1);

    // Lower mandible (crossed)
    translate([-18, 0, 10])
    rotate([0, 0, 45])
    scale([0.3, 0.3, 1])
    cylinder(h=15, r1=2, r2=1);

    // Eye
    translate([-14, 4, 12])
    sphere(1.5);

    // Crest
    translate([-12, 0, 20])
    rotate([0, 0, -5])
    cylinder(h=4, r1=3, r2=1);
}

// Wing with feather details
module wing() {
    // Main wing
    translate([2, 8, 8])
    rotate([0, 10, 0])
    scale([1, 0.3, 0.2])
    cylinder(h=20, r1=8, r2=3);

    // Wing feathers
    for(i = [0:3]) {
        translate([5 + i*3, 10, 8])
        rotate([0, 10 + i*5, 0])
        scale([0.8, 0.1, 0.1])
        cylinder(h=6 - i, r1=4, r2=1);
    }
}

// Leg and foot
module leg(side = 1) {
