// Dikdik - OpenSCAD medium complexity realistic model
// Body proportions based on Madoqua kirkii

$fn = 24; // Smooth curves for 3D printing

// Overall scale - ~80mm nose to tail
scale_factor = 1;

// Main body
module body() {
    difference() {
        union() {
            // Main torso
            translate([0, 0, 15])
            scale([1.2, 0.7, 1])
            sphere(15);

            // Rump
            translate([-20, 0, 12])
            scale([0.8, 0.6, 0.8])
            sphere(12);

            // Chest
            translate([15, 0, 10])
            scale([0.9, 0.7, 0.7])
            sphere(10);
        }

        // Leg holes
        for(side = [-1, 1])
        translate([5, side*8, 5])
        rotate([0, 90, 0])
        cylinder(h=10, d1=6, d2=8);
    }
}

// Head with distinctive dikdik features
module head() {
    translate([25, 0, 22])
    rotate([0, -10, 0])
    union() {
        // Main head
        scale([1.2, 0.8, 1])
        sphere(8);

        // Snout
        translate([8, 0, -2])
        scale([1.5, 0.6, 0.4])
        sphere(6);

        // Eyes
        for(side = [-1, 1])
        translate([4, side*5, 2])
        sphere(2);

        // Ears
        for(side = [-1, 1])
        translate([0, side*6, 4])
        rotate([0, 0, side*20])
        scale([0.3, 0.1, 1])
        cylinder(h=8, d1=4, d2=2);

        // Horns (small in females, still present)
        for(side = [-1, 1])
        translate([2, side*2, 5])
        rotate([0, 0, side*10])
        cylinder(h=6, d1=1.5, d2=0.5);
    }
}

// Leg module with hoof
module leg(height, angle) {
    rotate([0, 0, angle])
    union() {
        // Upper leg
        translate([0, 0, height/2])
        scale([0.8, 0.8, 1])
        cylinder(h=height, d1=5, d2=4);

        // Lower leg
        translate([0, 0, height-3])
        cylinder(h=height/2, d1=3.5, d2=3);

