// Dolphin model - medium complexity realistic style
// 3D printable dolphin approximately 80mm long

// Main parameters
body_length = 80;
body_height = 25;
body_width = 20;

// Body module
module body() {
    // Main torso
    translate([0, 0, body_height/2])
    scale([1, 0.7, 0.4])
    sphere(d=body_width, $fn=40);

    // Front taper
    translate([-body_length/2, 0, body_height/2])
    scale([1.5, 0.8, 0.5])
    rotate([0, 20, 0])
    cylinder(h=body_length/3, d1=body_width*0.8, d2=body_width*0.3, $fn=30);

    // Rear taper
    translate([body_length/2, 0, body_height/2])
    scale([1.5, 0.6, 0.3])
    rotate([0, -20, 0])
    cylinder(h=body_length/4, d1=body_width*0.7, d2=0, $fn=30);
}

// Tail flukes
module tail() {
    // Tail stock
    translate([body_length/2 + 5, 0, body_height/2 - 5])
    rotate([0, -30, 0])
    cylinder(h=15, d1=12, d2=8, $fn=20);

    // Upper fluke
    translate([body_length/2 + 18, 0, body_height/2])
    rotate([0, 0, 15])
    scale([1, 2.5, 0.2])
    sphere(d=12, $fn=30);

    // Lower fluke
    translate([body_length/2 + 18, 0, body_height/2 - 10])
    rotate([0, 0, -15])
    scale([1, 2.5, 0.2])
    sphere(d=12, $fn=30);
}

// Dorsal fin
module dorsal_fin() {
    translate([body_length/4, 0, body_height/2 + 8])
    rotate([0, 10, 0])
    linear_extrude(height=0.5)
    polygon(points=[
        [0, 0],
        [-5, 12],
        [5, 12],
        [0, 0]
    ]);
}

// Pectoral fins
module pectoral_fins() {
    // Left fin
    translate([-body_length/4, -body_width/2 - 5, body_height/2 - 8])
    rotate([0, -20, -45])
    scale([1.5, 1, 0.3])
    sphere(d=12, $fn=20);

    // Right fin
    translate([-body_length/4, body_width/2 + 5, body_height/2 - 8])
    rotate([0, -20, 45])
    scale([1.5, 1, 0.3])
    sphere(d=12, $fn=20);
}

// Head and beak
module head() {
    // Main head
    translate([-body_length/2 - 10, 0, body_height/2])
    scale([1.2, 0.8, 0.8])
    sphere(d=body_width, $fn=30);

    // Beak/snout
    translate([-body_length/2 - 20, 0, body_height/2 - 2])
    rotate([0, 10, 0])
    cylinder(h=15, d1=body_width*0.3, d2=body_width*0.1, $fn=20);
}

