// Curlew - Medium complexity realistic style
// Estimated print size: ~80mm length

// Color constants for preview
$fa = 2;
$fs = 0.5;

// Main body
module body() {
    difference() {
        union() {
            // Main torso
            scale([1.2, 0.8, 1]) sphere(d = 30, center = true);

            // Extended back/tail
            translate([10, 0, -5])
                scale([1.8, 0.6, 0.4])
                sphere(d = 20, center = true);

            // Chest area
            translate([-8, 0, -8])
                scale([0.8, 0.9, 0.6])
                sphere(d = 25, center = true);
        }

        // Neck hole
        translate([-15, 0, 5])
            rotate([0, 15, 0])
            cylinder(h = 20, d1 = 8, d2 = 12, center = true);
    }
}

// Head with long curved beak
module head() {
    translate([-25, 0, 15])
    rotate([0, 15, 0])
    union() {
        // Head
        sphere(d = 12, center = true);

        // Beak base
        translate([-10, 0, 0])
            scale([1.5, 0.4, 0.3])
            sphere(d = 15, center = true);

        // Beak extension
        hull() {
            translate([-10, 0, 0])
                sphere(d = 5, center = true);
            translate([-35, 0, 0])
                scale([0.4, 0.15, 0.15])
                sphere(d = 10, center = true);
        }

        // Eye
        translate([0, 4, 2])
            sphere(d = 2, center = true);
    }
}

// Wing module
module wing(side) {
    mirror([side, 0, 0])
    translate([0, 15, 5])
    rotate([0, -10, 20])
    difference() {
        union() {
            // Main wing shape
            scale([1.2, 0.3, 0.8])
            sphere(d = 25, center = true);

            // Wing tip
            translate([15, 0, 5])
                scale([0.8, 0.2, 0.6])
                sphere(d = 15, center = true);
        }

        // Wing feather detail (subtractive)
        for(i = [1:3]) {
            translate([5 + i*3, 0, -2])
                rotate([0, 0, i*10])
                scale([0.3, 0.1, 0.5])
                cube([10, 2, 8], center = true);
        }
    }
}

// Leg module
module leg(side) {
    mirror([side, 0, 0])
    translate([-5, 8, -18])
    union() {
        // Upper leg
        cylinder(h = 15, d1 = 4, d2 = 3);

        // Lower leg
        translate([0, 0, -16])
            rotate([10, 0, 0])
            cylinder(h = 12, d = 2.5);

        // Foot
        translate([0, 0, -28])
        union() {
            // Main foot
            scale([1.5, 0.3, 0.2])
            sphere(d = 8, center = true);

            // Toes
            for(i = [-1:1]) {
                translate([i*3, 0, -1])
                    scale([0.8, 0.2, 0.1])
                    sphere(d = 6, center = true);
            }
        }
    }
}

// Assembly
union() {
    body();
    head();
    wing(1);    // Right wing
    wing(-1);   // Left wing
    leg(1);     // Right leg
    leg(-1);    // Left leg
}

