// Drongo bird - medium complexity realistic style
// Designed for 3D printing with proper wall thickness

$fn = 32; // Smooth curves
body_length = 50;
wing_span = 70;
tail_length = 60;
head_size = 12;

module main_drongo() {
    union() {
        // Main body
        body();

        // Head
        translate([body_length/2 - 5, 0, 8])
            head();

        // Wings
        translate([-body_length/4, wing_span/2 - 5, 0])
            rotate([0, 0, 20])
            wing();
        translate([-body_length/4, -(wing_span/2 - 5), 0])
            rotate([0, 0, -20])
            mirror([0, 1, 0])
            wing();

        // Tail
        translate([-body_length/2 - tail_length/3, 0, 0])
            tail();

        // Legs
        translate([body_length/4, 5, -8])
            leg();
        translate([body_length/4, -5, -8])
            leg();
    }
}

module body() {
    // Main oval body
    scale([1.5, 1, 1])
    rotate([90, 0, 0])
    cylinder(h = body_length, d1 = 20, d2 = 15, center = true);

    // Chest protrusion
    translate([10, 0, 0])
    scale([0.8, 1.2, 0.8])
    sphere(r = 10);
}

module head() {
    union() {
        // Main head sphere
        sphere(r = head_size);

        // Beak
        translate([head_size + 5, 0, 0])
        rotate([0, -10, 0])
        cylinder(h = 12, d1 = 2, d2 = 4);

        // Eye sockets
        translate([head_size/2, head_size/2 - 2, head_size/2 - 2])
        sphere(r = 2);
        translate([head_size/2, -(head_size/2 - 2), head_size/2 - 2])
        sphere(r = 2);

        // Eyes
        translate([head_size/2 + 1, head_size/2 - 2, head_size/2 - 2])
        color("black")
        sphere(r = 1.5);
        translate([head_size/2 + 1, -(head_size/2 - 2), head_size/2 - 2])
        color("black")
        sphere(r = 1.5);
    }
}

module wing() {
    difference() {
        // Main wing shape
        rotate([0, 90, 0])
        scale([1, 0.3, 1])
        cylinder(h = wing_span/2, d1 = 8, d2 = 15);

        // Feather details
        for(i = [1:3]) {
            translate([5, i*5 - 5, -2])
            rotate([0, 0, -30])
            cube([8, 1, 4]);
        }
    }
}

module tail() {
    difference() {
        // Main tail
        rotate([0, 90, 0])
        scale([1, 0.15, 1])
        cylinder(h = tail_length, d1 = 12, d2 = 2);

        // Tail feather separation
        for(i = [-1:1]) {
            translate([0, i*3, 0])
            rotate([0, 0, i*10])
            cube([tail_length, 0.5, 6], center = true);
        }
    }
}

module leg() {
    // Upper leg
    cylinder(h = 8, d = 3);

    // Lower leg
    translate([0, 0, -8])
    cylinder(h = 10, d = 2);

    // Foot
    translate([0, 2, -16])
    rotate([90, 0, 0])
    union() {
        // Main foot
        cube([6, 2, 1], center = true);

        // Toes
        for(i = [-1, 0, 1]) {
            translate([i*2, 0, -1])
            cube([1, 2, 1], center = true);
        }
    }
}

// Generate the complete drongo
main_drongo();

