        difference() {
            translate([-20, 0, 12])
                rotate([0, 15, 0])
                scale([2, 0.6, 0.3])
                sphere(12);

            // Cut tail to be flat
            translate([-35, 0, 0])
                cube([30, 30, 30]);
        }

        // Wings - symmetrical pair
