module body_segment(w, h, l, taper=1) {
    hull() {
        translate([-l/2, 0, 0]) 
            scale([taper, 1, 1]) sphere(d=w);
        translate([l/2, 0, 0]) 
            sphere(d=w);
    }
}

module abdomen() {
    union() {
        // Segments 1-6
        for(i=[0:5]) {
            translate([0, 0, -i*4]) 
                body_segment(12-i, 8-i*0.5, 6, 1-i*0.08);
        }

        // Cerci (pincers)
        translate([0, 0, -24]) {
            difference() {
                hull() {
                    sphere(d=5);
                    translate([0, 0, -8]) cylinder(d1=5, d2=2, h=8);
                }
                // Cut gap between cerci
                translate([0, 0, -4]) cube([20, 0.5, 8], center=true);
            }
        }
    }
}

module thorax() {
    // Three thoracic segments
    translate([0, 0, 8]) body_segment(14, 10, 8);
    translate([0, 0, 14]) body_segment(12, 9, 7);
    translate([0, 0, 20]) body_segment(10, 8, 6);
}

module head() {
    union() {
        // Main head
        translate([0, 0, 26]) rotate([0, 15, 0]) 
            body_segment(10, 8, 8, 0.7);

        // Antennae
        for(side=[-1, 1]) {
            translate([side*4, 3, 29]) rotate([0, 30, 0]) {
                for(i=[0:10]) {
                    translate([0, 0, i*1.5]) 
                        rotate([0, 15, side*15]) 
                        cylinder(d1=1.2-i*0.08, d2=1-i*0.08, h=1.5);
                }
            }
        }

        // Mandibles
        translate([3, 0, 25]) rotate([0, 0, -20]) 
            cylinder(d1=2, d2=1, h=4);
        translate([-3, 0, 25]) rotate([0, 0, 20]) 
            cylinder(d1=2, d2=1, h=4);
    }
}

module legs() {
    for(side=[-1, 1]) {
        // Three pairs of legs
        for(pair=[0:2]) {
            translate([side*6, 0, 16-pair*6]) {
                rotate([0, 30+pair*10, 0]) {
                    // Coxa
                    cylinder(d=3, h=4);
                    translate([0, 0, 4]) rotate([30, 0, 0]) {
                        // Femur
                        cylinder(d=2.5, h=6);
                        translate([0, 0, 6]) rotate([60, 0, 0]) {
                            // Tibia
                            cylinder(d=2, h=7);
                            translate([0, 0, 7]) rotate([30, 0, 0]) {
                                // Tarsus
                                cylinder(d=1.5, h=5);
                            }
                        }
                    }
                }
            }
        }
    }
}

module wings() {
    // Elytra (wing covers) - simplified
    hull() {
        translate([6, 0, 5]) cylinder(d=1, h=1);
        translate([2, 0, -5]) cylinder(d=1, h=1);
        translate([6, 3, 0]) cylinder(d=1, h=1);
    }

    hull() {
        translate([-6, 0, 5]) cylinder(d=1, h=1);
        translate([-2, 0, -5]) cylinder(d=1, h=1);
        translate([-6, 3, 0]) cylinder(d=1, h=1);
    }
}

// Main assembly
union() {
    abdomen();
    thorax();
    head();
    legs();
    wings();
}

// Print orientation - flat on back
rotate([90, 0, 0]) {
    // The model is already oriented correctly
