// Echidna - medium complexity realistic style
// Roughly 70mm long, printable without supports

$fn = 36;

// Overall scale factor
scale_factor = 1.0;

// Main body
module body() {
    // Torso - elongated ellipsoid
    translate([0, 0, 10]) 
        scale([1.3, 0.8, 0.6]) 
            sphere(r = 20);

    // Rump - slightly smaller sphere
    translate([-25, 0, 10]) 
        scale([1.1, 0.9, 0.5]) 
            sphere(r = 15);
}

// Head with elongated snout
module head() {
    // Main head
    translate([28, 0, 15]) 
        scale([0.9, 0.8, 0.7]) 
            sphere(r = 12);

    // Snout/nose
    translate([38, 0, 12]) 
        scale([2.0, 0.5, 0.5]) 
            rotate([0, -15, 0]) 
                cylinder(h = 15, r1 = 8, r2 = 4);

    // Eyes
    translate([30, 6, 18]) sphere(r = 2);
    translate([30, -6, 18]) sphere(r = 2);
}

// Limbs - short with claws
module limb(x_pos, y_pos, z_pos, angle) {
    translate([x_pos, y_pos, z_pos]) {
        rotate([0, 0, angle]) {
            // Upper leg
            rotate([-30, 0, 0]) 
                scale([0.8, 0.8, 1.2]) 
                    cylinder(h = 8, r = 4);

            // Lower leg
