        for(i = [-1:1]) {
            translate([-25, i*8, -5])
            rotate([0, 10, 0])
            cube([30, 2, 15]);
        }
    }
}

module wing() {
    // Main wing structure
    difference() {
        union() {
            // Upper wing
            translate([0, 0, 0])
            rotate([0, 0, -10])
            scale([1, 2.5, 0.3])
            sphere(d=25);

            // Lower wing
            translate([-5, 15, -5])
            rotate([0, 0, -20])
            scale([0.8, 2, 0.2])
            sphere(d=20);

            // Wing tip
            translate([-10, 35, -8])
            rotate([0, 0, -30])
            cylinder(h=15, d1=8, d2=3);
