// Realistic Finch - Medium Complexity
// 3D printable finch model using basic OpenSCAD primitives

// Global parameters
body_length = 45;
body_height = 25;
body_width = 20;
wing_span = 40;
head_size = 12;
beak_length = 8;
tail_length = 25;
leg_height = 10;
foot_length = 5;

module finch() {
    union() {
        // Main body - elliptical shape
        body();

        // Head with beak
        head();

        // Wings
        left_wing();
        right_wing();

        // Tail
        tail();

        // Legs and feet
        left_leg();
        right_leg();

        // Eye details
        left_eye();
        right_eye();
    }
}

module body() {
    // Main body using scaled spheres
    translate([0, 0, leg_height + body_height/2])
    scale([1.4, 1, 1])
    sphere(d=body_height, $fn=32);

    // Chest bulge
    translate([body_length/4, 0, leg_height + body_height/3])
    scale([0.8, 0.7, 0.6])
    sphere(d=body_height, $fn=24);
}

module head() {
    translate([body_length/2 + head_size/2, 0, leg_height + body_height - 5])
    sphere(d=head_size, $fn=24);

    // Beak (conical)
    translate([body_length/2 + head_size + beak_length/2, 0, leg_height + body_height - 3])
    rotate([0, -15, 0])
    cylinder(h=beak_length, d1=2, d2=0.5, $fn=8);
}

module left_wing() {
    translate([0, -body_width/2 - 2, leg_height + body_height - 5])
    rotate([0, 30, -20])
    scale([1.2, 0.3, 1])
    sphere(d=wing_span/2, $fn=16);

    // Wing detail feathers
    for(i = [0:2]) {
        translate([-5 - i*3, -body_width/2 - 3 - i, leg_height + body_height - 3 - i])
        rotate([0, 35 - i*5, -20 + i*5])
        scale([1.2 - i*0.2, 0.2, 0.8 - i*0.1])
        sphere(d=wing_span/2 - i*3, $fn=12);
    }
}

module right_wing() {
    mirror([0, 1, 0])
    left_wing();
}

module tail() {
    // Tail feathers using stacked cylinders
    for(i = [0:3]) {
        translate([-body_length/2 - tail_length/2 + i*2, 0, leg_height + body_height/2 - 5 - i*2])
        rotate([0, 20 - i*5, 0])
        scale([1, 1 - i*0.1, 1])
        cylinder(h=tail_length - i*3, d1=body_width/2 - i*2, d2=body_width/4 - i, $fn=8);
    }
}

module left_leg() {
    translate([body_length/4, -body_width/4, leg_height/2])
    cylinder(h=leg_height, d=2, $fn=8);

    // Foot
    translate([body_length/4 + foot_length/2, -body_width/4 - foot_length/2, 0])
    rotate([0, 0, 30])
    cylinder(h=foot_length, d=1.5, $fn=6);

    // Toes
    for(i = [-1:1]) {
        translate([body_length/4 + foot_length, -body_width/4 - foot_length/2 + i*1.5, 0])
        rotate([0, 0, 30 + i*15])
        cylinder(h=foot_length/2, d=1, $fn=6);
    }
}

module right_leg() {
    mirror([0, 1, 0])
    left_leg();
}

module left_eye() {
    translate([body_length/2 + head_size/2 - 2, -head_size/4, leg_height + body_height - 1])
    sphere(d=1.5, $fn=8);

    // Eye highlight
    translate([body_length/2 + head_size/2 - 1.8, -head_size/4 - 0.3, leg_height + body_height - 0.5])
    sphere(d=0.5, $fn=6);
}

module right_eye() {
    mirror([0, 1, 0])
    left_eye();
}

// Render the complete finch
finch();

