// Realistic Medium-Complexity 3D-Printable Fly
// Body ~60 mm long, printable without supports

// Parameters
body_d = 12;
head_d = 8;
eye_d = 3.2;
thorax_l = 10;
abdomen_l = 22;
leg_thick = 1.2;
leg_len = 18;
wing_span = 34;
wing_thick = 0.6;

// Colors (for preview only)
color_body = "#222222";
color_wing = "#444444";
color_eye = "#000000";

// Body color
color(color_body) {

// Head
module head() {
    translate([0,0,head_d/2]) sphere(d=head_d,$fn=32);
    // Eyes
    color(color_eye) {
        translate([head_d/2.8,0,head_d*0.7]) sphere(d=eye_d,$fn=24);
        mirror([1,0,0]) translate([head_d/2.8,0,head_d*0.7]) sphere(d=eye_d,$fn=24);
    }
}

// Thorax
module thorax() {
    translate([0,0,-head_d/2-thorax_l/2]) {
        scale([1,0.8,1]) sphere(d=body_d,$fn=32);
    }
}

// Abdomen segments
module abdomen() {
    translate([0,0,-head_d/2-thorax_l-abdomen_l/2]) {
        difference() {
            scale([1,1.2,1.3]) sphere(d=body_d*1.2,$fn=32);
            // Segment grooves
            for(i=[1:5]) {
                translate([0,0,-abdominal_segment(i)]) {
                    rotate([90,0,0]) cylinder(h=body_d*1.3,d=body_d*1.2+1,$fn=32);
                }
            }
        }
    }
}

function abdominal_segment(i) = -ab abdomen_l/2 + i*4;

// Leg pair
module leg_pair(angle, side) {
    translate([0,side*body_d/2,-head_d/2-3]) {
        rotate([0,angle,0]) {
            rotate([0,0,side*20]) {
                // Coxa
                cylinder(h=3,d=leg_thick*1.4,$fn=12);
                translate([0,0,3]) rotate([side*-30,0,0]) {
                    // Femur
                    cylinder(h=leg_len*0.4,d=leg_thick,$fn=8);
                    translate([0,0,leg_len*0.4]) rotate([side*30,0,0]) {
                        // Tibia
                        cylinder(h=leg_len*0.5,d=leg_thick*0.8,$fn=8);
                        translate([0,0,leg_len*0.5]) rotate([side*-20,0,0]) {
                            // Tarsus
                            cylinder(h=leg_len*0.3,d=leg_thick*0.6,$fn=8);
                        }
                    }
                }
            }
        }
    }
}

// Wing
module wing() {
    color(color_wing) {
        translate([0,0,-head_d/2-4]) {
            rotate([0,0,90]) {
                linear_extrude(height=wing_thick) {
                    polygon(points=[
                        [0,0],
                        [wing_span*0.45,wing_span*0.05],
                        [wing_span*0.9,wing_span*0.15],
                        [wing_span*0.7,wing_span*0.3],
                        [wing_span*0.5,wing_span*0.35],
                        [wing_span*0.3,wing_span*0.2],
                        [0.1,0.1]
                    ]);
                }
            }
        }
    }
}

// Assembly
head();
thorax();
abdomen();

// Legs
for(side=[-1,1]) {
    leg_pair(-30,side);
    leg_pair(0,side);
    leg_pair(30,side);
}

// Wings
translate([0,0,-head_d/2-4]) {
    rotate([0,0,25]) wing();
    mirror([0,1,0]) rotate([0,0,25]) wing();
}

}

