// Ferret 3D Model - Medium Complexity Realistic Style
// Overall dimensions ~80mm length x 30mm height x 25mm width

// Body parameters
body_length = 60;
body_height = 20;
body_width = 18;

// Head parameters
head_length = 15;
head_height = 12;
head_width = 10;

// Leg parameters
leg_height = 8;
leg_width = 4;
leg_length = 6;

// Tail parameters
tail_length = 25;
tail_radius = 3;

// Ear parameters
ear_height = 6;
ear_width = 4;

// Nose parameters
nose_radius = 2;

// Main body - elongated oval shape
module body() {
    difference() {
        union() {
            // Main torso
            scale([1.8, 1, 1]) sphere(r=body_width/2, $fn=20);

            // Chest area
            translate([-body_length/4, 0, -body_height/4]) 
                scale([1.2, 0.8, 0.8]) sphere(r=body_width/2, $fn=16);

            // Rump area
            translate([body_length/3, 0, -body_height/6]) 
                scale([1.3, 0.9, 0.9]) sphere(r=body_width/2, $fn=16);
        }

        // Hollow out for 3D printing
        translate([0, 0, -2]) 
            scale([1.6, 0.8, 0.8]) sphere(r=body_width/2 - 2, $fn=20);
    }
}

// Head with snout
module head() {
    union() {
        // Main head
        translate([-head_length/2, 0, body_height/2]) 
            scale([1.2, 1, 1]) sphere(r=head_width/2, $fn=16);

        // Snout
        translate([-head_length - 2, 0, body_height/2 - 2]) 
            rotate([0, -15, 0]) 
            scale([1.5, 0.8, 0.6]) sphere(r=head_width/3, $fn=12);

        // Nose
        translate([-head_length - 3.5, 0, body_height/2 - 2]) 
            sphere(r=nose_radius, $fn=8);
    }
}

// Ears
module ears() {
    for(side = [-1, 1]) {
        translate([-head_length/2, side*head_width/2, body_height/2 + head_height/4]) 
            rotate([0, 20*side, 0]) 
            rotate([30, 0, 0]) 
            cylinder(h=ear_height, r1=ear_width/2, r2=0.5, $fn=8);
    }
}

// Eyes
module eyes() {
    for(side = [-1, 1]) {
        translate([-head_length/2 - 2, side*head_width/3, body_height/2]) 
            sphere(r=1, $fn=8);
    }
}

// Legs
module legs() {
    positions = [[-body_length/3, -body_width/2, -body_height/2], 
                 [body_length/3, -body_width/2, -body_height/2],
                 [-body_length/4, body_width/2, -body_height/2],
                 [body_length/3, body_width/2, -body_height/2]];

    for(pos = positions) {
        translate(pos) 
            rotate([0, 10, 0]) 
            scale([1, 0.8, 1]) 
            cylinder(h=leg_height, r=leg_width/2, $fn=8);
    }
}

// Paws
module paws() {
    positions = [[-body_length/3, -body_width/2, -body_height/2 - leg_height], 
                 [body_length/3, -body_width/2, -body_height/2 - leg_height],
                 [-body_length/4, body_width/2, -body_height/2 - leg_height],
                 [body_length/3, body_width/2, -body_height/2 - leg_height]];

    for(pos = positions) {
        translate(pos) 
            scale([1.2, 1, 0.5]) 
            sphere(r=leg_width/2, $fn=8);
    }
}

// Tail - curved tube
module tail() {
    hull() {
        translate([body_length/2, 0, -body_height/4]) 
            sphere(r=tail_radius, $fn=12);

        translate([body_length/2 + tail_length/3, -tail_length/4, -body_height/4 - 2]) 
            sphere(r=tail_radius*0.9, $fn=12);

        translate([body_length/2 + tail_length, -tail_length/2, -body_height/4 - 4]) 
            sphere(r=tail_radius*0.7, $fn=12);
    }
}

// Whiskers
module whiskers() {
    for(side = [-1, 1]) {
        for(angle = [0, 15, -15]) {
            translate([-head_length - 3, side*head_width/4, body_height/2 - 2]) 
                rotate([0, angle, 0]) 
                rotate([0, 0, side*angle]) 
                cylinder(h=8, r=0.3, $fn=4);
        }
    }
}

// Assembly
union() {
    body();

    translate([body_length/2 - head_length/2, 0, 0]) head();
    translate([body_length/2 - head_length/2, 0, 0]) ears();
    translate([body_length/2 - head_length/2, 0, 0]) eyes();

    legs();
    paws();

    tail();

    whiskers();
}

