// Fossa (Cryptoprocta ferox) - Realistic Style, Medium Complexity
// Designed for 3D printing, approximately 80mm long

// Overall dimensions
body_length = 80;
body_height = 25;
tail_length = 60;
leg_height = 20;

// Body module
module body() {
    // Main torso - elongated ellipsoid shape
    translate([-body_length/2, 0, 0])
    scale([1.5, 0.8, 0.6])
    sphere(d=body_height, $fn=32);

    // Chest bulge
    translate([-body_length/2 + 10, 0, -body_height/4])
    scale([1.2, 1, 0.8])
    sphere(d=body_height * 0.8, $fn=32);
}

// Head module
module head() {
    // Main head shape
    translate([body_length/2 - 5, 0, body_height/2])
    rotate([0, -10, 0])
    scale([1.2, 0.9, 0.8])
    sphere(d=body_height * 0.7, $fn=32);

    // Snout extension
    translate([body_length/2 + 8, 0, body_height/2 - 3])
    rotate([0, -10, 0])
    scale([1.5, 0.6, 0.5])
    sphere(d=body_height * 0.5, $fn=32);

    // Ears
    for(side = [-1, 1]) {
        translate([body_length/2, side * 8, body_height/2 + 5])
        rotate([0, 0, side * 20])
        scale([0.3, 0.8, 1.5])
        cylinder(h=8, d1=6, d2=4, $fn=16);
    }
}

// Leg module with paws
module leg(position, angle) {
    translate(position)
    rotate([0, 0, angle]) {
        // Upper leg
        translate([0, 0, leg_height/2])
        scale([0.8, 0.8, 1])
        cylinder(h=leg_height, d=6, $fn=16);

        // Lower leg
        translate([0, 0, -leg_height/2 - 2])
        scale([0.7, 0.7, 0.5])
        cylinder(h=8, d=5, $fn=16);

        // Paw
        translate([0, 0, -leg_height/2 - 8])
        scale([1.2, 1, 0.6])
        sphere(d=6, $fn=16);
    }
}

// Tail module
module tail() {
    // Segmented tail for realistic curve
    for(i = [0:5]) {
        translate([-body_length/2 - 5 - i * 10, 0, -body_height/4 + sin(i * 30) * 2])
        rotate([sin(i * 30) * 10, 0, 0])
        scale([1 - i * 0.1, 1 - i * 0.1, 1 - i * 0.1])
        sphere(d=body_height * 0.4 - i * 2, $fn=16);
    }
}

// Eyes module
module eyes() {
    for(side = [-1, 1]) {
        // Eye socket
        translate([body_length/2 + 5, side * 5, body_height/2])
        sphere(d=4, $fn=16);

        // Pupil
        translate([body_length/2 + 6.5, side * 5, body_height/2])
        sphere(d=2, $fn=16);
    }
}

// Main assembly
union() {
    // Body
    body();

    // Head
    head();

    // Legs
    leg([body_length/2 - 10, -8, -body_height/2], 10);
    leg([body_length/2 - 10, 8, -body_height/2], -10);
    leg([-body_length/2 + 15, -6, -body_height/2], 5);
    leg([-body_length/2 + 15, 6, -body_height/2], -5);

    // Tail
    tail();

    // Eyes
    eyes();
}

// Whiskers
for(side = [-1, 1]) {
    for(i = [0:2]) {
        translate([body_length/2 + 10, side * (5 + i * 2), body_height/2 - 2])
        rotate([0, side * 20, 0])
        cylinder(h=8, d=0.5, $fn=8);
    }
}

