$fa = 2;
$fs = 1;

module gannet() {
    scale(0.8) {
        // Main body
        body();

        // Head and neck
        translate([0, 22, 8]) rotate([-30, 0, 0]) head();

        // Wings
        translate([-12, 0, 5]) rotate([0, 0, 45]) wing();
        translate([12, 0, 5]) mirror([1, 0, 0]) rotate([0, 0, 45]) wing();

        // Tail
        translate([0, -25, 0]) tail();

        // Feet
        translate([-5, -15, -12]) foot();
        translate([5, -15, -12]) mirror([1, 0, 0]) foot();
    }
}

module body() {
    difference() {
        // Main body shape
        union() {
            scale([1.2, 1.5, 1]) sphere(12);
            translate([0, -5, -5]) scale([1, 1.2, 0.8]) sphere(10);
        }

        // Flatten bottom for printing
        translate([0, 0, -12]) cube([30, 30, 24], center=true);
    }
}

module head() {
    difference() {
        union() {
            // Main head shape
            translate([0, 8, 0]) scale([1, 1.3, 1]) sphere(5);

            // Beak
            translate([0, 14, -2]) rotate([30, 0, 0]) 
                scale([0.8, 2.5, 0.5]) cylinder(h=10, r1=2, r2=0.5);

            // Neck
            translate([0, 5, -3]) rotate([45, 0, 0])
                cylinder(h=8, r1=3, r2=2);
        }

        // Eye socket
        translate([-2.5, 8, 1]) sphere(1.2);
        translate([2.5, 8, 1]) sphere(1.2);
    }

    // Eyes
    color("black") {
        translate([-2.5, 7.8, 1.2]) sphere(0.8);
        translate([2.5, 7.8, 1.2]) sphere(0.8);
    }
}

module wing() {
    difference() {
        // Main wing
        translate([0, 0, 0]) scale([1, 2, 0.3]) sphere(8);

        // Wing tip
        translate([-5, -12, 0]) rotate([0, 0, -20])
            scale([0.5, 1, 0.3]) sphere(5);

        // Cut for feather definition
        for(i = [0:3]) {
            translate([0, -i*3, 0])
                rotate([0, 0, i*5])
                cube([15, 0.5, 2], center=true);
        }
    }
}

module tail() {
    difference() {
        // Tail shape
        translate([0, -5, 0]) scale([1.5, 1, 0.2]) sphere(6);

        // Tail fork
        translate([0, -8, 0]) cube([12, 8, 3], center=true);
    }
}

module foot() {
    // Leg
    translate([0, 0, 3]) rotate([0, 10, 0])
        scale([0.4, 0.4, 1]) cylinder(h=6, r=3);

