// Realistic Frog - Medium Complexity
// Dimensions: ~80mm long, 50mm wide, 30mm tall

// Global parameters
body_length = 40;
body_width = 35;
body_height = 25;
leg_thickness = 6;
head_size = 22;

// Main body module
module frog_body() {
    // Main torso - ellipsoid shape using scaled spheres
    translate([0, 0, body_height/2])
    scale([1.2, 1, 0.8])
    sphere(d = body_width, $fn=50);

    // Lower body bulge
    translate([0, 0, body_height/4])
    scale([1.4, 1.2, 0.6])
    sphere(d = body_width * 0.9, $fn=50);

    // Back ridge
    for(i = [-1:0.5:1]) {
        translate([i * 10, 0, body_height * 0.9])
        rotate([90, 0, 0])
        cylinder(h = 8, d1 = 4, d2 = 2, $fn=6);
    }
}

// Head module
module frog_head() {
    // Main head sphere
    translate([-body_length/2 - head_size/2, 0, head_size/2])
    sphere(d = head_size, $fn=50);

    // Snout extension
    translate([-body_length/2 - head_size/1.2, 0, head_size/2])
    scale([1.5, 0.8, 0.8])
    sphere(d = head_size * 0.7, $fn=40);
}

// Eye module
module eye(side) {
    translate([-body_length/2 - head_size/1.5, side * head_size/3, head_size * 0.8]) {
        // Eye socket
        color("darkgreen")
        sphere(d = 8, $fn=20);

        // Eye ball
        translate([0, 0, 3])
        color("black")
        sphere(d = 5, $fn=20);

        // Eyelid ridge
        translate([0, 0, 6])
        scale([1.2, 1, 0.3])
        color("green")
        sphere(d = 8, $fn=20);
    }
}

// Leg module
module leg(position, angle, mirror_x = false) {
    // Position: 1 for front, -1 for back
    // angle: rotation for sitting pose

    translate([position * body_length/2.5, 0, 0]) {
        rotate([0, angle, 0]) {
            if(mirror_x) mirror([1, 0, 0]) {
                // Upper leg
                translate([0, 0, leg_thickness/2])
                rotate([0, -30, 0])
                scale([1.5, 1, 0.8])
                sphere(d = leg_thickness * 2, $fn=20);

                // Lower leg
                translate([-leg_thickness * 1.2, 0, -leg_thickness * 1.5])
                rotate([0, 30, 0])
                scale([2, 0.8, 0.6])
                sphere(d = leg_thickness * 1.8, $fn=20);

                // Foot
                translate([-leg_thickness * 2.5, 0, -leg_thickness * 2.5])
                scale([3, 1.5, 0.5])
                sphere(d = leg_thickness * 1.5, $fn=20);

                // Toes
                for(toe = [-1, 0, 1]) {
                    translate([-leg_thickness * 3.5, toe * leg_thickness/2, -leg_thickness * 2.7])
                    scale([0.8, 0.3, 0.2])
                    sphere(d = leg_thickness, $fn=10);
                }
            } else {
                // Upper leg
                translate([0, 0, leg_thickness/2])
                rotate([0, -30, 0])
                scale([1.5, 1, 0.8])
                sphere(d = leg_thickness * 2, $fn=20);

                // Lower leg
                translate([-leg_thickness * 1.2, 0, -leg_thickness * 1.5])
                rotate([0, 30, 0])
                scale([2, 0.8, 0.6])
                sphere(d = leg_thickness * 1.8, $fn=20);

                // Foot
                translate([-leg_thickness * 2.5, 0, -leg_thickness * 2.5])
                scale([3, 1.5, 0.5])
                sphere(d = leg_thickness * 1.5, $fn=20);

                // Toes
                for(toe = [-1, 0, 1]) {
                    translate([-leg_thickness * 3.5, toe * leg_thickness/2, -leg_thickness * 2.7])
                    scale([0.8, 0.3, 0.2])
                    sphere(d = leg_thickness, $fn=10);
                }
            }
        }
    }
}

// Main assembly
union() {
    // Body
    color("forestgreen")
    frog_body();

    // Head
    color("forestgreen")
    frog_head();

    // Eyes
    eye(-1); // Left
    eye(1);  // Right

    // Front legs
    leg(1, 45, false);
    leg(1, 45, true);

    // Back legs
    leg(-1, 60, false);
    leg(-1, 60, true);
}

