module gar_head() {
    translate([-gar_length/2, 0, 0])
    difference() {
        // Elongated snout
        scale([1.2, 0.3, 0.2])
            sphere(gar_length/4, $fn=32);

        // Mouth opening
        translate([-gar_length/8, 0, -gar_length/20])
            rotate([0, 30, 0])
            cube([gar_length/12, gar_length/25, gar_length/40], center=true);
    }
}

module gar_jaw() {
    translate([-gar_length/2 + 5, 0, -gar_height/2])
    rotate([15, 0, 0])
    scale([0.8, 0.25, 0.15])
        sphere(gar_length/5, $fn=16);
}

module gar_eye() {
    translate([-gar_length/2 + 8, gar_length/10, gar_height/4])
    difference() {
        sphere(2.5);
        translate([0, 0, 2])
            sphere(1.5);
    }
}

module gar_fin(position, angle, scale_factor) {
    translate(position)
    rotate([0, 0, angle])
    scale([scale_factor, 1, 1])
    difference() {
        // Fin base
        translate([0, 0, -1])
            cube([8, 1, 2]);

        // Fin rays
        for(i = [-3:3]) {
            translate([i*1.5, 0, 0])
                rotate([0, 90, 0])
                cylinder(h=4, r1=0.3, r2=0.1, $fn=8);
        }
    }
}

module gar_scales() {
    // Simple scale pattern along body
    for(i = [0:5:gar_length-10]) {
        translate([-gar_length/2 + i, 0, 0])
        rotate([0, 0, i*5])
        difference() {
            scale([0.8, 0.3, 0.2])
                sphere(4);
            translate([0, 0, -1])
                cube([8, 6, 2], center=true);
        }
    }
}

module gar_tail() {
    translate([gar_length/2, 0, 0])
    rotate([0, 0, -10])
    difference() {
        // Upper tail lobe
        translate([0, 0, 2])
            rotate([0, 30, 0])
            scale([1, 0.3, 0.1])
                cylinder(h=12, r1=6, r2=3, $fn=3);

        // Lower tail lobe
        translate([0, 0, -2])
            rotate([0, -30, 0])
            scale([1, 0.3, 0.1])
                cylinder(h=8, r1=4, r2=2, $fn=3);
    }
}

// Assemble the gar
union() {
    gar_body();
    gar_head();
    gar_jaw();
    gar_eye();
    mirror([0, 1, 0]) gar_eye();

