    gar_fin([-gar_length/4, -gar_length/8, -gar_height/4], -45, 0.7);

    // Anal fin
    gar_fin([gar_length/3, 0, -gar_height/2], 180, 0.8);

    gar_tail();
    gar_scales();
}

