// Gallinule (medium complexity, realistic style)
// Total size ~80 mm from beak to tail

// Body dimensions
body_len = 55;
body_w = 30;
body_h = 28;

// Head dimensions
head_r = 12;

// Beak dimensions
beak_len = 18;
beak_w = 4;
beak_h = 5;

// Leg dimensions
leg_len = 25;
leg_r = 2.5;
toe_len = 8;
toe_r = 1.2;

// Wing dimensions
wing_len = 30;
wing_w = 15;

// Tail dimensions
tail_len = 15;
tail_w = 25;
tail_h = 8;

// Helper module for rounded body
module rounded_box(l, w, h, r) {
    hull() {
        translate([r, r, r]) sphere(r);
        translate([l-r, r, r]) sphere(r);
        translate([r, w-r, r]) sphere(r);
        translate([l-r, w-r, r]) sphere(r);
        translate([r, r, h-r]) sphere(r);
        translate([l-r, r, h-r]) sphere(r);
        translate([r, w-r, h-r]) sphere(r);
        translate([l-r, w-r, h-r]) sphere(r);
    }
}

// Main body
module body() {
    difference() {
        union() {
            // Main torso
            translate([0, -body_w/2, 0])
                rounded_box(body_len, body_w, body_h, 8);

            // Neck transition
            translate([body_len-5, 0, body_h-3])
                rotate([0, -20, 0])
                cylinder(h=12, r1=8, r2=6, $fn=16);
        }

        // Eye sockets (left and right)
        for(side = [-1, 1])
            translate([body_len+8, side*8, body_h+5])
                sphere(3.5);
    }
}

// Head with beak
module head_and_beak() {
    translate([body_len+8, 0, body_h+5])
    rotate([0, -20, 0]) {
        // Head
        sphere(head_r, $fn=32);

        // Beak
        translate([head_r, 0, -head_r/3])
        rotate([0, -10, 0])
        difference() {
            // Main beak
            cube([beak_len, beak_w, beak_h], center=true);

            // Beak tip taper
            translate([beak_len-2, 0, -beak_h/2])
                cube([4, beak_w+1, beak_h], center=true);
        }

        // Eyes
        color("white")
        for(side = [-1, 1])
            translate([head_r/2, side*4, head_r/3])
                sphere(2.5, $fn=16);
    }
}

// Wings
module wings() {
    for(side = [-1, 1]) {
        translate([body_len/3, side*(body_w/2+1), body_h/2])
        rotate([0, 0, side*(-20)])
        rotate([0, 90, 0])
            difference() {
                // Main wing
                scale([1, 0.6, 1])
                    cylinder(h=wing_len, r1=wing_w/2, r2=wing_w/3, $fn=16);

                // Feather notches
                for(i = [1:3]) {
                    translate([wing_len/4*i, -wing_w/2-1, 0])
                        rotate([0, 0, 45])
                        cube([wing_len/8, wing_w, wing_w], center=true);
                }
            }
    }
}

// Tail
module tail() {
    translate([-5, 0, body_h/2])
    rotate([0, 0, 0])
    difference() {
        // Main tail fan
        cube([tail_len, tail_w, tail_h], center=true);

        // Tail notch
        translate([0, 0, tail_h/2])
            cube([tail_len+1, tail_w/2, tail_h+1], center=true);
    }
}

// Legs and feet
module legs_and_feet() {
    for(side = [-1, 1]) {
        translate([body_len/4, side*body_w/3, 0])
        rotate([0, side*5, 0])
        union() {
            // Upper leg
            cylinder(h=leg_len, r=leg_r, $fn=12);

            // Foot
            translate([0, 0, -leg_len])
            rotate([90, 0, 0])
            union() {
                // Central toe
                translate([0, 0, 0])
                    cylinder(h=toe_len, r=toe_r, $fn=8);

                // Side toes
                for(angle = [-30, 30])
                    rotate([0, 0, angle])
                        translate([toe_len/3, 0, 0])
                            cylinder(h=toe_len*0.8, r=toe_r, $fn=8);
            }
        }
    }
}

// Assembly
union() {
    body();
    head_and_beak();
    wings();
    tail();
    legs_and_feet();
}

