$fn=36;

// Gharial 3D model - medium complexity realistic style
// Designed for 3D printing, approximately 80mm long

module gharial() {
    // Body parameters
    body_length = 60;
    body_height = 12;
    body_width = 15;

    // Head parameters
    head_length = 25;
    head_height = 8;

    // Tail parameters
    tail_length = 35;
    tail_height = 6;

    // Leg parameters
    leg_length = 8;
    leg_height = 4;

    // Main body
    union() {
        // Central body
        translate([0, 0, body_height/2])
        scale([1, 0.8, 1])
        sphere(d=body_height);

        // Body segments
        for(i=[-20:10:20]) {
            translate([i, 0, body_height/2 - abs(i)/8])
            sphere(d=body_height - abs(i)/4);
        }

        // Head
        translate([-body_length/2 - head_length/2, 0, body_height/2])
        scale([1.2, 0.7, 0.9])
        sphere(d=head_height);

        // Snout
        translate([-body_length/2 - head_length - 8, 0, body_height/2 + 1])
        scale([3, 0.3, 0.3])
        cylinder(h=15, d1=head_height/2, d2=2);

        // Tail
        for(i=[body_length/2:5:body_length/2 + tail_length]) {
            translate([i, 0, body_height/2 - (i - body_length/2)/5])
            scale([1, 1 - (i - body_length/2)/tail_length, 1 - (i - body_length/2)/tail_length])
            sphere(d=tail_height);
        }

        // Legs
        module leg(pos_x, pos_y, angle) {
            translate([pos_x, pos_y, leg_height/2])
            rotate([0, 0, angle])
            union() {
                // Upper leg
                scale([1.5, 0.8, 1])
                sphere(d=leg_height);

                // Lower leg
                translate([leg_length/2, 0, -leg_height/2])
                rotate([90, 0, 0])
                cylinder(h=leg_length, d=leg_height*0.8);

                // Foot
                translate([leg_length, 0, -leg_height])
                scale([2, 0.5, 0.5])
                sphere(d=leg_height);
            }
        }

        // Front legs
        leg(-body_length/3, body_width/2, 45);
        leg(-body_length/3, -body_width/2, -45);

        // Back legs
        leg(body_length/3, body_width/2, -45);
        leg(body_length/3, -body_width/2, 45);

        // Eyes
        translate([-body_length/2 - head_length/2 - 3, 3, body_height/2 + 2])
        sphere(d=2);

        translate([-body_length/2 - head_length/2 - 3, -3, body_height/2 + 2])
        sphere(d=2);

        // Nostrils
        translate([-body_length/2 - head_length - 10, 1.5, body_height/2 + 1])
        sphere(d=1);

        translate([-body_length/2 - head_length - 10, -1.5, body_height/2 + 1])
        sphere(d=1);

        // Scutes (back ridges)
        for(i=[-15:5:15]) {
            translate([i, 0, body_height/2 + 2])
            scale([0.3, 1, 1])
            cylinder(h=3, d1=2, d2=0.5);
        }
    }
}

// Create the gharial
gharial();

