// Goby fish in realistic style with medium complexity

// Overall dimensions
body_length = 80;
body_height = 20;
body_width = 15;

// Main body module
module goby_body() {
    // Tapered body
    difference() {
        hull() {
            // Front part - more rounded
            translate([body_length/2 - 5, 0, 0])
                scale([1.5, 1, 1])
                sphere(d=body_width);

            // Rear part - flatter
            translate([-body_length/2 + 5, 0, 0])
                scale([0.8, 0.7, 0.4])
                sphere(d=body_width);
        }

        // Carve out belly cavity
        translate([0, 0, -body_height/2])
            cube([body_length, body_width*2, body_height], center=true);
    }
}

// Head with mouth and eyes
module goby_head() {
    // Head shape
    translate([body_length/2 - 10, 0, 0])
    rotate([0, -10, 0])
    scale([1.2, 1, 0.9])
    sphere(d=body_width*1.2);

    // Mouth
    translate([body_length/2 - 5, 0, -body_height/4])
    rotate([0, -30, 0])
    scale([0.8, 0.3, 0.2])
    cylinder(h=body_width/2, d1=body_width/3, d2=body_width/4);

    // Eyes
    for(side = [-1, 1]) {
        translate([body_length/2 - 8, side*body_width/3, body_height/4])
        sphere(d=body_width/5);

        // Pupils
        translate([body_length/2 - 7.5, side*body_width/3, body_height/4])
        scale([0.5, 0.5, 0.7])
        color("black")
        sphere(d=body_width/8);
    }
}

// Dorsal fins
module dorsal_fins() {
    // First dorsal fin
    translate([-body_length/4, 0, body_height/2])
    rotate([0, 0, 0])
    for(i = [0:2]) {
        translate([i*3, 0, 0])
        rotate([0, 0, -5 + i*5])
        scale([0.8, 0.2, 1])
        cylinder(h=body_height/1.5, d1=body_width/3, d2=0.5);
    }

    // Second dorsal fin
    translate([-body_length/2 + 10, 0, body_height/2.5])
    for(i = [0:5]) {
        translate([i*2.5, 0, 0])
        rotate([0, 0, -3 + i*3])
        scale([0.9, 0.15, 1])
        cylinder(h=body_height/2, d1=body_width/4, d2=0.3);
    }
}

// Pectoral fins
module pectoral_fins() {
    for(side = [-1, 1]) {
        translate([body_length/3, side*body_width/2, -body_height/4])
        rotate([0, 0, side*30])
        rotate([side*10, 0, 0])
        scale([1, 0.3, 0.2])
        cylinder(h=body_width*1.2, d1=body_width/2, d2=body_width/4);

        // Fin rays
        for(i = [0:3]) {
            translate([body_length/3 + 2, side*(body_width/2 + i*2), -body_height/4 - 1])
            rotate([0, 90, 0])
            scale([0.1, 0.1, 1])
            cylinder(h=body_width/1.5, d=0.5);
        }
    }
}

// Tail fin
module tail_fin() {
    translate([-body_length/2 + 2, 0, 0])
    rotate([0, 10, 0])
    union() {
        // Upper lobe
        translate([0, 0, body_height/4])
        rotate([0, 0, -20])
        scale([1.5, 0.2, 1])
        cylinder(h=body_width, d1=body_width/2, d2=0.3);

        // Lower lobe
        translate([0, 0, -body_height/4])
        rotate([0, 0, 20])
        scale([1.5, 0.2, 1])
        cylinder(h=body_width, d1=body_width/2, d2=0.3);

        // Central part
        translate([0, 0, 0])
        scale([0.5, 0.15, 0.8])
        sphere(d=body_width/1.5);
    }
}

// Pelvic fins (fused into suction cup)
module pelvic_suction_cup() {
    translate([0, 0, -body_height/2 - 2])
    difference() {
        // Outer cup
        cylinder(h=3, d1=body_width*1.2, d2=body_width*0.8);

        // Inner cavity
        translate([0, 0, -0.5])
        cylinder(h=4, d1=body_width, d2=body_width*0.6);
    }
}

// Body patterning
module body_pattern() {
    // Spots along body
    for(i = [0:8]) {
        translate([body_length/2 - 15 - i*6, body_width/4 - (i%2)*body_width/2, body_height/4])
        scale([1, 0.7, 0.3])
        sphere(d=body_width/6);
    }
}

// Assemble the goby
union() {
    goby_body();
    goby_head();
    dorsal_fins();
    pectoral_fins();
    tail_fin();
    pelvic_suction_cup();
    body_pattern();
}

