// Goshawk 3D Model - Realistic Style, Medium Complexity
// Overall size ~80mm wingspan

// Main parameters
$fn = 32;
body_length = 40;
body_width = 20;
body_height = 30;
wing_span = 80;
tail_length = 35;
head_size = 15;

// Head module with beak
module head() {
    translate([body_length/2 - 5, 0, body_height/2 + 2]) {
        // Main head shape
        scale([1.2, 1, 1.1]) sphere(d = head_size);

        // Beak
        translate([head_size/2, 0, -head_size/4]) {
            rotate([0, -30, 0]) {
                difference() {
                    cylinder(h = head_size/1.5, r1 = head_size/3.5, r2 = 0);
                    translate([0, 0, -1]) cube([head_size, head_size, 2], center = true);
                }
            }
        }

        // Eye sockets
        for(i = [-1, 1]) {
            translate([head_size/4, i * head_size/3, 0]) {
                rotate([0, 90, 0]) 
                cylinder(h = head_size/10, r = head_size/8);
            }
        }
    }
}

// Body module
module body() {
    translate([0, 0, body_height/2]) {
        scale([1.5, 1, 1]) sphere(d = body_width);

        // Chest detail
        translate([-body_length/4, 0, -body_height/2]) {
            scale([1.2, 0.8, 0.5]) sphere(d = body_width);
        }
    }
}

// Wing module with feather details
module wing(side) {
    mirror([0, side, 0]) {
        translate([-body_length/4, side * body_width/2, body_height/2]) {
            rotate([0, 0, side * -20]) {
                // Main wing shape
                scale([2.5, 0.5, 0.15]) sphere(d = wing_span/2);

                // Primary feathers
                for(i = [0:4]) {
                    translate([wing_span/3 + i * 5, 0, -2 - i * 0.5]) {
                        rotate([0, side * (10 + i * 5), 0]) {
                            scale([0.8 - i * 0.1, 0.1, 0.05]) 
                            cube([15 - i * 2, 10, 2]);
                        }
                    }
                }

                // Secondary feathers
                for(i = [0:3]) {
                    translate([wing_span/6 - i * 3, 0, -1 - i * 0.3]) {
                        scale([1, 0.1, 0.05]) 
                        cube([12 - i * 2, 8, 1.5]);
                    }
                }
            }
        }
    }
}

// Tail module
module tail() {
    translate([-body_length/2 - tail_length/2, 0, body_height/3]) {
        rotate([20, 0, 0]) {
            // Main tail
            scale([2.5, 1, 0.15]) sphere(d = tail_length);

            // Tail feathers
            for(i = [-1.5, -0.5, 0.5, 1.5]) {
                translate([0, i * 8, -2]) {
                    scale([0.3, 0.3, 0.05]) 
                    cube([tail_length * 0.9, 4, 1]);
                }
            }
        }
    }
}

// Legs and feet
module legs() {
    for(side = [-1, 1]) {
        translate([0, side * body_width/3, -body_height/2]) {
            // Upper leg
            cylinder(h = 8, r1 = 3, r2 = 2);

            // Lower leg
            translate([0, 0, -8]) {
                rotate([side * 15, 0, 0]) {
                    cylinder(h = 10, r = 2);

                    // Foot
                    translate([0, 0, -10]) {
                        rotate([0, 90, 0]) {
                            scale([1.5, 1, 0.5]) sphere(d = 8);

                            // Toes
                            for(angle = [0, 45, -45]) {
                                rotate([0, angle, 0]) {
                                    translate([4, 0, 0]) {
                                        scale([1, 0.3, 0.3]) cube([5, 2, 2]);
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}

// Main assembly
union() {
    body();
    head();
    wing(1);
    wing(-1);
    tail();
    legs();
}

