// Grouse - Medium Complexity Realistic Style
// Approx 70mm long x 40mm tall x 35mm wide

$fn = 32;
body_length = 45;
body_height = 25;
tail_length = 25;
wing_span = 35;

module grouse() {
    // Main body - oval shape
    union() {
        // Body core
        translate([0, 0, body_height/2])
            scale([1.2, 0.8, 1])
            sphere(d=body_height);

        // Back extension
        translate([-body_length/4, 0, body_height/2-3])
            scale([1.5, 0.9, 0.8])
            sphere(d=body_height-5);

        // Breast bulge
        translate([body_length/4, 0, body_height/2-5])
            scale([0.8, 0.7, 0.6])
            sphere(d=body_height-3);
    }

    // Head
    translate([body_length/2+5, 0, body_height-2])
        rotate([0, -10, 0])
        scale([0.7, 0.6, 0.6])
        sphere(d=body_height-10);

    // Tail fan
    module tail_feather(angle) {
        rotate([0, angle, 0])
        translate([-body_length/2-tail_length/2, 0, body_height/2-8])
            scale([1, 0.1, 0.3])
            cube([tail_length, wing_span/3, 8], center=true);
    }

    for(i = [-30:15:30]) {
        tail_feather(i);
    }

    // Wings
    module wing(side) {
        mirror([side, 0, 0])
        translate([0, wing_span/4, body_height/2-3])
        rotate([20, 0, -10])
        scale([1.2, 0.3, 0.6])
        cube([body_length/2, wing_span/2, body_height/3], center=true);
    }

    wing(1);
    wing(-1);

    // Legs
    module leg(side) {
        translate([body_length/6, side*8, 0])
        union() {
            // Thigh
            cylinder(h=8, d1=5, d2=4);
            translate([0, 0, 8])
                rotate([0, 30, 0])
                cylinder(h=10, d1=4, d2=3);

            // Feet
            translate([0, side*3, -2])
                rotate([0, 90, 0])
                scale([1.5, 0.8, 1])
                cylinder(h=8, d=3);

            // Toes
            for(j = [-1, 0, 1]) {
                translate([j*2.5, side*3, -2])
                    rotate([0, 90, j*20])
                    cylinder(h=4, d=2);
            }
        }
    }

    leg(1);
    leg(-1);

    // Beak
    translate([body_length/2+12, 0, body_height-2])
        rotate([0, -20, 0])
        scale([1, 0.5, 0.3])
        cylinder(h=6, d=4);

    // Eye
    translate([body_length/2+6, 3.5, body_height])
        sphere(d=2.5);

    // Neck ruff
    for(k = [0:30:330]) {
        rotate([0, 0, k])
        translate([body_length/3, 0, body_height/2+3])
            scale([0.3, 0.1, 0.5])
            cylinder(h=6, d=3);
    }
}

