// Guanaco - Medium complexity realistic style
// Size: ~75mm long

// Main body with oval shape
module body() {
    difference() {
        // Main torso
        scale([1.2, 0.8, 0.6]) sphere(d=40);

        // Neck cavity
        translate([15, 0, 5]) rotate([0, -20, 0]) 
            scale([1, 1, 0.8]) cylinder(h=15, d1=12, d2=10, $fn=16);
    }
}

// Neck connecting to head
module neck() {
    translate([20, 0, 12]) rotate([0, -20, 0]) {
        // Neck base
        scale([1, 0.8, 1]) cylinder(h=15, d=10, $fn=12);

        // Neck to head transition
        translate([0, 0, 8]) rotate([0, -10, 0]) 
            cylinder(h=8, d1=10, d2=6, $fn=8);
    }
}

// Head with long snout
module head() {
    translate([30, 0, 20]) rotate([0, -30, 0]) {
        // Main head sphere
        scale([1.2, 0.8, 0.9]) sphere(d=12);

        // Snout extension
        translate([5, 0, -2]) rotate([0, -10, 0])
            scale([1.5, 0.7, 0.6]) sphere(d=8);

        // Ears - two triangular shapes
        translate([0, 5, 4]) rotate([0, -10, 30]) 
            scale([0.3, 0.1, 0.8]) cylinder(h=6, d=4, $fn=3);
        translate([0, -5, 4]) rotate([0, -10, -30]) 
            scale([0.3, 0.1, 0.8]) cylinder(h=6, d=4, $fn=3);

        // Eyes
        translate([3, 2.5, 2]) sphere(d=1.5);
        translate([3, -2.5, 2]) sphere(d=1.5);
    }
}

// Leg module with tapering
module leg(height, angle) {
    rotate([0, angle, 0]) {
        // Upper leg
        translate([0, 0, height/2]) 
            scale([0.8, 0.8, 1]) cylinder(h=height, d=6, $fn=8);

        // Lower leg
        translate([0, 0, height]) 
            scale([0.6, 0.6, 1]) cylinder(h=height*0.8, d=5, $fn=6);

        // Hoof
        translate([0, 0, height*1.8]) 
            cylinder(h=3, d1=5, d2=4, $fn=4);
    }
}

// Tail with curve
module tail() {
    translate([-22, 0, 8]) rotate([0, 20, 0]) {
        for(i=[0:2]) {
            translate([i*2, -i*0.5, 0]) rotate([0, i*5, 0])
                scale([1-i*0.1, 1-i*0.1, 1]) sphere(d=4-i);
        }
    }
}

// Main assembly
union() {
    body();
    neck();
    head();
    tail();

    // Front legs
    translate([10, 8, -8]) leg(20, 15);
    translate([10, -8, -8]) leg(20, 15);

    // Rear legs
    translate([-10, 8, -8]) leg(22, 5);
    translate([-10, -8, -8]) leg(22, 5);
}

