// Realistic medium-complexity 3D printable gull
// Approx 80 mm wingspan, 55 mm beak to tail

$fn = 32;  // Smooth curves for printing

module gull() {
    difference() {
        union() {
            // Main body – elongated sphere
            translate([0, 0, 8])
                scale([1.3, 0.7, 1])
                sphere(12);

            // Tail – tapered cylinder
            translate([-20, 0, 7])
                rotate([0, 15, 0])
                cylinder(h = 15, r1 = 7, r2 = 2);

            // Neck
            translate([10, 0, 12])
                rotate([20, 0, 0])
                cylinder(h = 8, r1 = 5, r2 = 4);

            // Head
            translate([15, 0, 18])
                sphere(6);

            // Beak – cone
            translate([21, 0, 18])
                rotate([15, 0, 0])
                cylinder(h = 10, r1 = 2.5, r2 = 0.5);

            // Left wing
            translate([0, -12, 12])
                rotate([0, 0, -25])
                scale([1, 0.15, 0.8])
                sphere(15);

            // Right wing
            mirror([0, 1, 0])
            translate([0, -12, 12])
                rotate([0, 0, -25])
                scale([1, 0.15, 0.8])
                sphere(15);

            // Webbed feet – two simple flat shapes
            // Left foot
            translate([5, -6, 0])
                rotate([0, 0, -20])
                scale([1.5, 0.3, 0.2])
                cube([8, 6, 2]);

            // Right foot
            translate([5, 6, 0])
                rotate([0, 0, 20])
                scale([1.5, 0.3, 0.2])
                cube([8, 6, 2]);

            // Tail feathers – three thin plates
            for(y = [-3, 0, 3])
                translate([-25, y, 7])
                    rotate([0, 15, 0])
                    scale([0.3, 1, 0.3])
                    cube([10, 4, 1]);
        }

        // Eye sockets (left & right)
        translate([17, -2.5, 20])
            sphere(1.2);
        translate([17, 2.5, 20])
            sphere(1.2);

        // Cut underside for printing stability
        translate([0, 0, -1])
            cube([60, 40, 2], center = true);
    }
}

// Assemble the gull upright
gull();

