// Gorilla - Realistic Style, Medium Complexity
// Dimensions: ~80mm height when printed

module rounded_cube(size, radius) {
    // Create a cube with rounded edges
    translate([radius, radius, 0]) 
        cylinder(h=size.z, r=radius, $fn=16);
    translate([radius, size.y-radius, 0]) 
        cylinder(h=size.z, r=radius, $fn=16);
    translate([size.x-radius, radius, 0]) 
        cylinder(h=size.z, r=radius, $fn=16);
    translate([size.x-radius, size.y-radius, 0]) 
        cylinder(h=size.z, r=radius, $fn=16);

    translate([0, radius, radius]) 
        cube([size.x, size.y-radius*2, size.z-radius*2]);
    translate([radius, 0, radius]) 
        cube([size.x-radius*2, size.y, size.z-radius*2]);
    translate([radius, radius, radius]) 
        cube([size.x-radius*2, size.y-radius*2, size.z]);
}

module torso() {
