        translate([40, -8, -5]) back_left_leg();
        translate([40, 8, -5]) back_right_leg();

        // Bead pattern along back
        for(i = [0:7]) {
            translate([8 + i*8, 0, 4]) scale([1.2, 0.8, 0.6]) sphere(2.5);
        }
    }
}

