// Realistic 3D printable grub (larva) model
// Size: ~60 mm long, 25 mm diameter

// Overall grub body with segments
module grub_body() {
    union() {
        // Head segment
        translate([0, 0, 5])
            scale([1.3, 1.3, 1.2])
                sphere(d=12, $fn=24);

        // Thoracic segments (3 segments)
        for (i = [0:2]) {
            translate([0, 0, -i*8 - 3])
                scale([1.2 - i*0.1, 1.2 - i*0.1, 1])
                    sphere(d=14, $fn=24);
        }

        // Abdominal segments (6 segments)
        for (i = [0:5]) {
            translate([0, 0, -i*6 - 27])
                scale([1.1 - i*0.15, 1.1 - i*0.15, 0.8])
                    sphere(d=12 - i*1.2, $fn=20);
        }

        // Tail segment
        translate([0, 0, -65])
            scale([0.7, 0.7, 1.5])
                sphere(d=6, $fn=16);
    }
}

// Mandibles (mouthparts)
module mandibles() {
    translate([0, 0, 8])
    union() {
        // Left mandible
        translate([-3, 4, 0])
            rotate([0, -20, -15])
                cube([2, 6, 4], center=true);

        // Right mandible
        translate([3, 4, 0])
            rotate([0, -20, 15])
                cube([2, 6, 4], center=true);
    }
}

// Spiracles (breathing holes along sides)
module spiracles() {
    for (i = [0:8]) {
        translate([7, 0, -i*7 - 5])
            rotate([0, 90, 0])
                cylinder(h=2, d1=1.5, d2=2, $fn=8);
    }
}

// Leg stubs (reduced legs typical of grubs)
module leg_stubs() {
    for (i = [0:2]) {
        translate([6, 0, -i*8])
            rotate([0, 45, 0])
                cylinder(h=4, d1=3, d2=2, $fn=8);
    }
}

// Body segmentation rings
module segmentation_rings() {
    difference() {
        // Main body
        grub_body();

        // Hollow out segmentation grooves
        for (i = [0:9]) {
            translate([0, 0, -i*6 - 8])
                scale([1.1, 1.1, 0.1])
                    sphere(d=15, $fn=32);
        }
    }
}

