    // Main body - tapered ellipsoid
    translate([0, 0, 8])
    scale([1.0, 0.6, 0.7])
    sphere(d = body_length);

    // Head - slightly larger sphere
    translate([-body_length/2 - 3, 0, 8])
    scale([1.1, 0.8, 0.8])
    sphere(d = 12);

    // Dorsal fin base
    translate([5, 0, 16])
    rotate([0, 90, 0])
    cylinder(h = 8, d1 = 6, d2 = 2);
