    union() {
        // Upper lobe
        translate([body_length/2, 0, 8])
        rotate([0, 0, 30])
        scale([1.5, 1, 0.2])
        sphere(d = tail_width);

        // Lower lobe
        translate([body_length/2, 0, 8])
        rotate([0, 0, -30])
        scale([1.5, 1, 0.2])
        sphere(d = tail_width);

        // Connection between lobes
