// Realistic Hedgehog - Medium Complexity
// ~70mm long, 3D printable with minimal supports

// Main parameters
body_length = 70;
body_height = 35;
body_width = 40;
spike_length = 8;
spike_diameter = 1.8;

// Body module - ellipsoid shape
module hedgehog_body() {
    // Main body - slightly flattened sphere
    scale([1.3, 1, 0.8])
        sphere(d=body_width, $fn=32);

    // Snout extension
    translate([body_length/2 - 5, 0, -5])
        scale([1.5, 1, 0.8])
            sphere(d=15, $fn=24);
}

// Ear module
module ear(side) {
    translate([body_length/4, side * body_width/3, body_height/2])
        rotate([0, side * 15, 0])
            scale([0.3, 0.8, 1])
                sphere(d=8, $fn=12);
}

// Leg module
module leg(pos_x, pos_y, angle) {
    translate([pos_x, pos_y, -body_height/2 + 3])
        rotate([angle, 0, 0])
            cylinder(h=10, d1=6, d2=4, $fn=8);
}

// Eye module
module eye(side) {
    translate([body_length/3, side * body_width/4, body_height/4])
        sphere(d=3.5, $fn=16);

    // Pupil
    translate([body_length/3 + 2, side * body_width/4, body_height/4])
        sphere(d=2, $fn=8);
}

// Spike module
module spike(x, y, angle, length) {
    translate([x, y, 0])
        rotate([0, angle, atan2(y, x)])
            cylinder(h=length, d=spike_diameter, $fn=6);
}

// Main hedgehog assembly
module hedgehog() {
    union() {
        // Body
        color("saddlebrown")
        hedgehog_body();

        // Ears
        color("tan")
        ear(1);
        ear(-1);

        // Legs
        color("sienna") {
            leg(body_length/3, body_width/3, 15);
            leg(body_length/3, -body_width/3, 15);
            leg(-body_length/3, body_width/3, -15);
            leg(-body_length/3, -body_width/3, -15);
        }

        // Eyes
        color("white")
        eye(1);
        color("white")
        eye(-1);

        // Nose
        color("black")
        translate([body_length/2 + 5, 0, -3])
            sphere(d=4, $fn=16);
    }

    // Spikes covering back
    color("dimgray")
    for (i = [0:5:360]) {
        for (j = [0:10:body_length-10]) {
            if (j > -body_length/2 + 15) {
                translate([j, 0, 0])
                    rotate([0, 0, i])
                        translate([0, body_width/2 - 5, body_height/2 - 5])
                            spike(0, 0, -20, spike_length);
            }
        }
    }

    // Additional spikes on sides
    for (i = [0:15:360]) {
        rotate([0, 0, i])
            translate([0, body_width/2 - 2, 0])
                for (z = [0:5:body_height-5]) {
                    spike(0, 0, 0, spike_length * 0.8);
                }
    }
}

// Generate the model
hedgehog();

