// Honeybee - Realistic Medium Complexity
// Size: ~80mm wingspan, 50mm body length
// 3D printable with 0.8mm minimum walls

// Body segments
module head() {
    union() {
        // Main head sphere
        sphere(d=8, $fn=20);

        // Mandibles
        translate([-2, -4, 0]) rotate([0, 0, -30]) cylinder(h=3, d1=2, d2=1, $fn=8);
        translate([2, -4, 0]) rotate([0, 0, 30]) cylinder(h=3, d1=2, d2=1, $fn=8);

        // Compound eyes
        translate([-3, 3, 2]) sphere(d=3, $fn=12);
        translate([3, 3, 2]) sphere(d=3, $fn=12);
    }
}

module thorax() {
    union() {
        // Main thorax
        scale([1.2, 1, 0.8]) sphere(d=12, $fn=20);

        // Wing attachments
        for(side = [-1, 1]) {
            translate([side*6, 0, 2]) rotate([0, 30, 0]) 
                scale([1, 3, 0.3]) sphere(d=3, $fn=6);
        }
    }
}

module abdomen() {
    // Tapered abdomen with segments
    union() {
        for(i = [0:5]) {
            translate([0, -i*3-2, 0]) {
                color(i % 2 == 0 ? [1, 0.9, 0] : [0.1, 0.1, 0.1])
                    sphere(d=12-i, $fn=16);
            }
        }
    }
}

// Wings
module wing() {
    difference() {
        // Main wing shape
        translate([0, 0, 0]) 
            scale([1, 3, 0.1]) sphere(d=15, $fn=8);

        // Wing veins (negative space)
        for(i = [1:4]) {
            rotate([0, 0, i*20-40]) 
                translate([0, i*2, 0.2]) 
                    cube([0.5, 10, 0.3], center=true);
        }
    }
}

// Legs
module leg(position, angle) {
    translate(position) rotate([0, 0, angle]) {
        // Coxa
        cylinder(h=3, d=2, $fn=8);
        translate([0, 3, 0])
            rotate([30, 0, 0]) {
                // Femur
                cylinder(h=6, d=1.5, $fn=8);
                translate([0, 6, 0])
                    rotate([30, 0, 0]) {
                        // Tibia
                        cylinder(h=8, d=1.2, $fn=8);
                        translate([0, 8, 0])
                            rotate([45, 0, 0]) {
                                // Tarsus
                                cylinder(h=5, d=1, $fn=6);
                            }
                    }
            }
    }
}

// Antennae
module antenna() {
    for(side = [-1, 1]) {
        translate([side*2, 4, 3]) rotate([0, -30, 0]) {
            cylinder(h=8, d=0.8, $fn=8);
            translate([0, 0, 8]) sphere(d=1.2, $fn=8);
        }
    }
}

// Main assembly
union() {
    // Position head
    translate([0, 25, 0]) head();

    // Position thorax
    translate([0, 15, 0]) thorax();

    // Position abdomen
    translate([0, -5, 0]) abdomen();

    // Wings
    for(side = [-1, 1]) {
        translate([side*6, 15, 4]) rotate([0, 0, side*30]) 
            rotate([30, 0, 0]) wing();
        translate([side*4, 15, 3]) rotate([0, 0, side*20]) 
            rotate([45, 0, 0]) scale([0.7, 0.7, 0.7]) wing();
    }

    // Legs
    for(i = [0:2]) {
        angle = i*30 - 30;
        leg([0, 15-i*2, -4], angle);
        leg([0, 15-i*2, 4], angle);
    }

    // Antennae
    translate([0, 25, 0]) antenna();
}

