helmet_height = 8;
helmet_width = 12;

module guineafowl() {
    union() {
        // Main body - oval shape
        translate([0, 0, body_height/2])
            scale([1.2, 0.8, 1])
                sphere(d=body_height);

        // Neck
        translate([body_length/2 - 10, 0, body_height - 5])
            rotate([0, -20, 0])
                cylinder(h=neck_length, d=neck_diameter, $fn=8);

        // Head
        translate([body_length/2 + 10, 0, body_height + neck_length - 2])
            sphere(d=head_diameter);

        // Helmet (casque)
        translate([body_length/2 + 15, 0, body_height + neck_length + 5])
            rotate([0, 15, 0])
                cylinder(h=helmet_height, d1=helmet_width, d2=helmet_width/2, $fn=6);

        // Left wing
        translate([-5, body_width/2, body_height - 5])
            rotate([0, -10, -20])
                scale([1, 0.3, 0.1])
                    cube([wing_length, wing_width, 5]);

        // Right wing
        mirror([0, 1, 0]) {
            translate([-5, body_width/2, body_height - 5])
                rotate([0, -10, -20])
                    scale([1, 0.3, 0.1])
                        cube([wing_length, wing_width, 5]);
        }

        // Tail
        translate([-body_length/2 - 5, 0, body_height/2 + 5])
            rotate([0, 20, 0])
                scale([1, 0.6, 0.4])
                    cube([tail_length, tail_height, tail_height/2]);

        // Left leg
        translate([body_length/4, body_width/3 - 5, 0])
            cylinder(h=leg_length, d=leg_diameter, $fn=8);

        // Left foot
        translate([body_length/4 + 5, body_width/3 - 8, -leg_length])
            rotate([0, 0, 20])
                cube([foot_length, foot_width, 2]);

        // Right leg
        translate([body_length/4, -body_width/3 + 5, 0])
            cylinder(h=leg_length, d=leg_diameter, $fn=8);

        // Right foot
        translate([body_length/4 + 5, -body_width/3 + 8, -leg_length])
            rotate([0, 0, -20])
                cube([foot_length, foot_width, 2]);

        // Beak
        translate([body_length/2 + 20, 0, body_height + neck_length])
            rotate([0, -20, 0])
                cylinder(h=8, d1=4, d2=2, $fn=6);

        // Eye details (left)
        translate([body_length/2 + 12, 6, body_height + neck_length + 3])
            sphere(d=3);

        // Eye details (right)
        translate([body_length/2 + 12, -6, body_height + neck_length + 3])
            sphere(d=3);

        // Spots on body
