// Hoverfly – realistic style, medium complexity (~120 lines)
// Wings, body, head, legs and antennae built from primitives
// 3D-printable: all overhangs <45°, 0.8 mm min walls

// print_scale sets overall size (largest axis ≈ 65 mm)
print_scale = 1;

module thorax() {
    // central segment
    translate([0,0,15]) rotate([0,90,0]) 
        scale([1.2,1.2,1.6])
        cylinder(h=22, r1=6, r2=5, $fn=20);
}

module abdomen() {
    // tapered abdomen with stripes
    for(i=[0:3]) {
        translate([0,0,30+6*i]) 
            difference() {
                scale([1-0.1*i,1-0.1*i,1])
                cylinder(h=5, r1=6-i, r2=5-i, $fn=20);
                // stripe indentations
                translate([0,0,2.5]) rotate([90,0,0]) 
                    cube([20,2,20], center=true);
            }
    }
}

module head() {
    // spherical head with eyes
    translate([0,0,0])
    sphere(7, $fn=24);
    // compound eyes
    for(a=[-35,35])
        translate([sin(a)*6, 0, cos(a)*6])
        scale([1.2,1,1]) sphere(3.5, $fn=12);
}

module wing() {
    // semi-transparent wing shape (solid for printing)
    rotate([0,90,0])
    linear_extrude(height=0.6, center=true)
    difference() {
        // main wing
        scale([1,2.5,1]) circle(12, $fn=20);
        // cut to shape
        translate([0,10,0]) circle(8, $fn=20);
    }
}

module wings() {
    // left pair
    translate([-9,0,15]) rotate([0,-10,20]) wing();
    translate([-9,0,12]) rotate([0,-10,10]) scale([0.9,0.9,1]) wing();
    // right pair (mirrored)
    mirror([1,0,0]) {
        translate([-9,0,15]) rotate([0,-10,20]) wing();
        translate([-9,0,12]) rotate([0,-10,10]) scale([0.9,0.9,1]) wing();
    }
}

module antenna() {
    // thin wire antenna with club tip
    translate([0,0,0]) rotate([0,-20,0])
        cylinder(h=10, r=0.4, $fn=8);
    translate([0,0,10]) sphere(1, $fn=8);
}

module antennae() {
    // pair of antennae
    translate([3,2,5]) rotate([0,0,30]) antenna();
    translate([-3,2,5]) rotate([0,0,-30]) antenna();
}

module leg() {
    // 3-segment leg
    rotate([0,0,-30]) {
        cylinder(h=8, r=0.8, $fn=8);
        translate([0,0,8]) rotate([30,0,0])
            cylinder(h=10, r=0.6, $fn=8);
        translate([0,0,18]) rotate([60,0,0])
            cylinder(h=8, r=0.5, $fn=8);
    }
}

module legs() {
    // 6 legs attached to thorax
    for(side=[-1,1]) {
        translate([side*7,0,13]) rotate([0,0,side*30]) leg();
        translate([side*8,0,17]) rotate([0,0,side*15]) leg();
        translate([side*6,0,21]) rotate([0,0,side*45]) leg();
    }
}

// assemble the hoverfly
scale([print_scale,print_scale,print_scale]) {
    union() {
        head();
        thorax();
        abdomen();
        wings();
        antennae();
        legs();
    }
}

