// Hornbill - Medium complexity realistic style
// Overall dimensions: 90mm length, 40mm height, 30mm width

$fn = 32;

module hornbill() {
    // Main body parameters
    body_length = 40;
    body_height = 25;
    body_width = 20;

    // Head parameters
    head_length = 30;
    head_height = 20;
    head_width = 15;

    // Casque (horn) parameters
    casque_height = 12;
    casque_width = 8;
    casque_length = 20;

    // Beak parameters
    beak_length = 25;
    beak_height = 10;
    beak_width = 8;

    // Tail parameters
    tail_length = 15;
    tail_height = 18;

    // Leg parameters
    leg_height = 8;
    leg_width = 3;

    // Main body - ellipsoid shape
    translate([0, 0, 0])
        scale([body_length/2, body_width/2, body_height/2])
        sphere(1);

    // Head - slightly smaller ellipsoid
    translate([body_length/2 + head_length/2 - 5, 0, body_height/2 - 5])
        scale([head_length/2, head_width/2, head_height/2])
        sphere(1);

    // Casque (characteristic horn on top of beak)
    translate([body_length/2 + head_length - 5, 0, body_height/2 + head_height/2 - 2])
        difference() {
            scale([casque_length/2, casque_width/2, casque_height/2])
            sphere(1);
            // Hollow out casque slightly
            translate([0, 0, -2])
                scale([casque_length/2 - 1, casque_width/2 - 1, casque_height/2 - 1])
                sphere(1);
        }

    // Upper beak
    translate([body_length/2 + head_length + beak_length/2 - 10, 0, body_height/2 - 2])
        rotate([0, -10, 0])
        scale([beak_length/2, beak_width/2, beak_height/2])
        sphere(1);

    // Lower beak
    translate([body_length/2 + head_length + beak_length/2 - 10, 0, body_height/2 - 10])
        rotate([0, -10, 0])
        scale([beak_length/2 - 2, beak_width/2 - 1, beak_height/2 - 3])
        sphere(1);

    // Tail - fan shape
    translate([-body_length/2 - tail_length/2, 0, 0])
        rotate([0, 0, -5])
        scale([tail_length/2, tail_width/2, tail_height/2])
        sphere(1);

    // Wings - folded against body
    wing_pos = body_length/4;
    for(side = [-1, 1]) {
        translate([wing_pos, side * (body_width/2 - 2), body_height/4])
            rotate([20 * side, 0, 10 * side])
            scale([body_length/3, body_width/4, body_height/3])
            difference() {
                sphere(1);
                // Create wing tip
                translate([body_length/6, 0, 0])
                    sphere(0.8);
            }
    }

    // Legs - simple cylinders
    leg_offset = body_length/3;
    for(side = [-1, 1]) {
        translate([leg_offset, side * (body_width/4), -body_height/2])
            cylinder(h = leg_height, r = leg_width/2);

        // Feet - simple flat cylinders
        translate([leg_offset, side * (body_width/4), -body_height/2 - leg_height])
            rotate([90, 0, 0])
            cylinder(h = leg_width * 2, r = leg_width/2);
    }

    // Eye - small sphere
    translate([body_length/2 + head_length/2, head_width/2 - 2, body_height/2 + head_height/2 - 5])
        sphere(1.5);

    // Eye - other side
    translate([body_length/2 + head_length/2, -head_width/2 + 2, body_height/2 + head_height/2 - 5])
        sphere(1.5);

    // Neck transition
    translate([body_length/4, 0, body_height/4])
        scale([body_length/3, body_width/3, body_height/3])
        sphere(1);
}

// Add tail width for better proportion
tail_width = 25;

// Main assembly
hornbill();

