// Total length ~65mm, height ~30mm, width ~25mm

// Body parameters
body_length = 35;
body_height = 20;
body_width = 18;

// Head parameters
head_length = 15;
head_width = 12;
head_height = 10;

// Leg parameters
leg_height = 12;
leg_width = 5;
leg_depth = 6;

// Tail parameters
tail_length = 8;
tail_width = 6;
tail_height = 5;

// Ear parameters
ear_size = 4;

// Eye parameters
eye_radius = 1.5;

// Nose parameters
nose_width = 3;
nose_height = 2;
nose_depth = 2;

// Fur texture parameters
fur_radius = 0.5;
fur_height = 0.3;

module hyrax_body() {
    // Main torso - slightly flattened ellipsoid
    scale([1, 0.7, 0.6])
    sphere(d=body_width, $fn=32);

    // Lower belly bulge
    translate([0, -body_height*0.3, -body_height*0.2])
    scale([1.2, 0.8, 0.7])
    sphere(d=body_width*0.9, $fn=32);
}

module hyrax_head() {
    // Main head shape
    translate([body_length/2 + head_length/2, 0, body_height/2 + head_height/2 - 3])
    rotate([0, -10, 0])
    scale([1.1, 0.9, 0.8])
    sphere(d=head_width, $fn=32);

    // Snout extension
    translate([body_length/2 + head_length, 0, body_height/2 + head_height/2 - 3])
    scale([1.5, 0.7, 0.7])
    sphere(d=head_width*0.7, $fn=32);

    // Nose
    translate([body_length/2 + head_length + 5, 0, body_height/2 + head_height/2 - 3])
    scale([nose_width/3, nose_height/2, nose_depth/2])
    sphere(d=nose_width, $fn=16);
}

module hyrax_ear(left_right = 1) {
    // Ear base
    translate([body_length/2 + head_length/2 - 2, left_right * head_width/2, body_height/2 + head_height - 2])
    rotate([0, 10 * left_right, 0])
    cylinder(h=ear_size, d1=ear_size*1.2, d2=ear_size*0.8, $fn=16);

    // Inner ear
    translate([body_length/2 + head_length/2 - 2.2, left_right * head_width/2, body_height/2 + head_height - 1.5])
    rotate([0, 10 * left_right, 0])
    cylinder(h=ear_size*0.7, d1=ear_size*0.8, d2=ear_size*0.5, $fn=16);
}

module hyrax_leg(position = 1, side = 1) {
    // Front/back position
    x_pos = position == 1 ? body_length/3 : -body_length/3;

    // Main leg
    translate([x_pos, side * body_width/2 - 2, -leg_height/2])
    rotate([10, 0, side * 5])
    cylinder(h=leg_height, d=leg_width, $fn=16);

    // Foot
    translate([x_pos + (position == 1 ? 2 : -2), side * body_width/2 - 2, -leg_height - 1])
    rotate([0, 0, side * 5])
