    sphere(r = 11);
}

module legs() {
    module leg(x, y, angle) {
        translate([x, y, 0]) {
            rotate([0, 0, angle]) {
                // Upper leg
                translate([0, 0, 8])
                rotate([-15, 0, 0])
                cylinder(h = 15, r1 = 4, r2 = 3);

                // Lower leg
                translate([0, -3, 8])
                rotate([15, 0, 0])
                cylinder(h = 18, r1 = 3, r2 = 2);

                // Hoof
                translate([0, -6, 8])
                rotate([0, 0, 0])
                cylinder(h = 5, r1 = 2.5, r2 = 2);
            }
        }
    }

    // Front legs
    leg(8, 20, 0);
    leg(-8, 20, 0);

    // Back legs
    leg(7, -15, 0);
    leg(-7, -15, 0);
}

module head() {
    translate([0, 30, 25])
    rotate([0, -10, 0]) {
        // Skull base
        scale([0.8, 1.2, 0.9])
        sphere(r = 8);

