// Impala - medium complexity realistic style
// Body proportions based on Aepyceros melampus
// Overall length ~90mm, height ~45mm, width ~20mm

$fn = 24;
wall_thickness = 1.2;

module impala() {
    // Main body
    color("burlywood") union() {
        // Torso - elongated oval
        translate([0, 0, 20]) scale([1.8, 0.8, 1]) sphere(r=15);

        // Belly - slightly flattened
        translate([0, 0, 12]) scale([2, 0.9, 0.5]) sphere(r=12);

        // Chest forward
        translate([-25, 0, 15]) scale([0.8, 0.7, 1]) sphere(r=10);

        // Rump
        translate([25, 0, 20]) scale([1, 0.9, 1.1]) sphere(r=12);
    }

    // Neck
    translate([-15, 0, 30]) rotate([0, -15, 0]) scale([0.8, 0.6, 0.8]) 
        cylinder(h=20, r1=6, r2=4);

    // Head
    translate([-30, 0, 35]) rotate([0, -20, 0]) {
        // Skull
        scale([1.5, 0.7, 0.8]) sphere(r=8);

        // Snout
        translate([-8, 0, -2]) scale([0.8, 0.5, 0.4]) sphere(r=6);

        // Ears - lyre shaped
        translate([0, 6, 5]) rotate([0, 30, 20]) 
            scale([0.2, 0.05, 1]) cylinder(h=12, r=4);
        translate([0, -6, 5]) rotate([0, 30, -20]) 
            scale([0.2, 0.05, 1]) cylinder(h=12, r=4);

        // Horns - males only, lyre-shaped
        translate([2, 2, 8]) rotate([0, -10, 30]) 
            scale([0.3, 0.3, 1]) cylinder(h=15, r1=1, r2=0.5);
        translate([2, -2, 8]) rotate([0, -10, -30]) 
            scale([0.3, 0.3, 1]) cylinder(h=15, r1=1, r2=0.5);
    }

    // Legs
    module leg(x, y, angle, length) {
        translate([x, y, 15]) rotate([0, 0, angle]) {
            // Upper leg
            rotate([0, -angle, 0]) 
                scale([0.5, 0.5, 1]) cylinder(h=length*0.6, r=3);

            // Lower leg
            translate([0, 0, -length*0.6]) rotate([30, 0, 0]) 
                scale([0.4, 0.4, 1]) cylinder(h=length*0.4, r=2.5);

            // Hoof
            translate([0, 0, -length]) 
                scale([0.6, 0.4, 0.3]) cylinder(h=3, r=2);
        }
    }

    // Front legs
    leg(-20, 6, 0, 25);
    leg(-20, -6, 0, 25);

    // Hind legs - longer
    leg(20, 6, 0, 30);
    leg(20, -6, 0, 30);

    // Tail
    translate([28, 0, 25]) rotate([30, 0, 0]) {
        // Tail base
        scale([0.8, 0.8, 0.5]) sphere(r=4);

        // Tail tuft
        translate([0, 0, -8]) scale([0.3, 0.3, 1]) cylinder(h=10, r=3);
    }

    // Facial features
    translate([-36, 2, 33]) sphere(r=1); // Eye
    translate([-36, -2, 33]) sphere(r=1); // Eye

    // Nostrils
    translate([-38, 1.5, 31]) sphere(r=0.8);
    translate([-38, -1.5, 31]) sphere(r=0.8);
}

// Main assembly
impala();

