// Realistic Insect - Ant
// Approx 75mm length, 3D printable with no overhangs

$fn = 32;
body_color = "black";

module thorax_segment() {
    // Single thorax segment - ellipsoid shape
    scale([1.2, 0.8, 0.6])
        sphere(d=10);
}

module abdomen_segment() {
    // Abdomen segments - larger ellipsoids
    scale([1.5, 1.2, 1])
        sphere(d=12);
}

module leg_joint() {
    // Ball joint for legs
    sphere(d=3);
}

module leg_segment(length, angle) {
    // Single leg segment - tapered cylinder
    rotate([0, 0, angle])
        rotate([90, 0, 0])
            cylinder(h1=2, h2=1, r1=1.5, r2=1, h=length);
}

module antenna_segment(length) {
    // Antenna segment - thin cylinder
    rotate([0, 90, 0])
        cylinder(d=0.8, h=length);
}

module mandible() {
    // Sharp mandibles
    difference() {
        rotate([0, 90, 0])
            cylinder(h1=3, h2=1, r1=2, r2=0.5, h=6);
        translate([0, 0, 1])
            rotate([0, 90, 0])
                cylinder(d=1, h=7);
    }
}

module compound_eye() {
    // Cluster of small spheres for compound eye
    for(i=[0:5]) {
        rotate([0, 0, i*60])
            translate([3, 0, 0])
                sphere(d=2);
    }
}

module complete_leg(side, position) {
    // Build complete six-jointed leg
    mirror([side, 0, 0]) {
        translate([5, 0, -position*3]) {
            leg_joint();
            translate([0, 0, -1.5])
                leg_segment(8, 30);
            translate([0, -7, -2])
                leg_segment(10, -20);
            translate([0, -14, -3])
                leg_segment(12, 15);
        }
    }
}

// Main body assembly
union() {
    color(body_color) {
        // Head
        translate([0, 30, 0])
            scale([1, 1.2, 0.9])
                sphere(d=10);

        // Compound eyes
        translate([0, 30, 0]) {
            translate([4, 2, 3])
                compound_eye();
            translate([-4, 2, 3])
                mirror([-1, 0, 0])
                    compound_eye();
        }

        // Mandibles
        translate([0, 30, 0]) {
            translate([2, 5, -2])
                rotate([0, 20, 10])
                    mandible();
            translate([-2, 5, -2])
                    rotate([0, -20, -10])
                        mandible();
        }

        // Antennae
        translate([0, 30, 0]) {
            translate([2, 5, 4])
                antenna_segment(15);
            translate([-2, 5, 4])
                antenna_segment(15);
        }

        // Thorax (3 segments)
        translate([0, 20, 0])
            thorax_segment();
        translate([0, 15, 0])
            thorax_segment();
        translate([0, 10, 0])
            thorax_segment();

        // Abdomen (4 segments, larger)
        translate([0, 0, 0])
            abdomen_segment();
        translate([0, -8, 0])
            abdomen_segment();
        translate([0, -16, 0])
            scale([1.3, 1.4, 1.2])
                abdomen_segment();
        translate([0, -26, 0])
            scale([1.2, 1.3, 1.1])
                abdomen_segment();

        // Six legs (3 pairs)
        for(i=[0:2]) {
            complete_leg(1, i);
            complete_leg(-1, i);
        }
    }
}

